css:
@-moz-document domain("latexbase.com") {

body {
  background-color: #222;
  color: #aaa;
}

a {
  color: #5291f8; /* from cppreference */
}

/*** Topmost Header ***/
.topbar {
  background-color: #282828;
  border-bottom-color: #333;
  color: #aaa;
}

.topbar .topbar-brand, .topbar .dropdown, .topbar .topbar-action {
  color: #aaa;
}

/*** Sliding Pane on clicking on 'â‰¡' on the top ***/

.menu-premium-overlay {
  background-color: #282828;
}

.app.shadow {
  box-shadow: -2px 2px 2px 0 #333; /* only color changed */
}

/*** 2nd Header - Document Name ***/

.metadata {
  background-color: #222;
  border-bottom-color: #333;
}

.document-name-value {
  background-color: #222;
}

.metadata-word-count-label {
  color: #999;
}

.metadata-word-count-value {
  color: #666;
}

/** Status **/
.metadata-status {
  background-color: #1c1c1c;
}

.status-green {
  color: #009a00;
}

/*** 3rd Header left - LaTeX Editor, Preview ***/

.panel-header {
  background-color: #222;
  border-bottom-color: #333;
}

.panel1 {
  border-right-color: #666;
}

/** Buttons **/

.btn-default {
  background-image: linear-gradient(to bottom,#282828 0,#222 100%);
  border-color: #333;
  color: #666;
  text-shadow: none;
}

.btn-default:hover, .btn-default:focus {
  background-color: #333;
  background-position: 0 0px;
  border-color: #333;
  color: #aaa;
}

.btn-yellow {
  background-color: #333;
  border-color: #333;
  color: #4183C4;
}

.btn-yellow:hover {
  background-color: #666;
}

/* Settings dropdown */
.settings {
  background-color: #222;
}

/** Editor Line No.s **/
.ace-ambiance .ace_gutter {
  background-color: #202020;
  border-right-color: #333;
  color: #555;
  text-shadow: none;
}

/** Editor Auto-complete dropdown **/

.ace_editor.ace_autocomplete {
  background-color: #222;
  border-color: #333;
  color: #999;
}

.ace_editor.ace_autocomplete .ace_marker-layer .ace_active-line {
  background-color: #333;
}

.ace_editor.ace_autocomplete .ace_completion-highlight {
  color: #aaa;
}

/** Preview Log **/
.preview-log {
  background-color: #282828;
  color: #aaa;
}

/** Settings overlay **/
.modal {
  background-color: #222;
  color: #aaa;
}

.modal-header {
  background-color: #282828;
}

.modal-logo {
  background-color: #222;
}

.modal-exit {
  background-color: #222;
  color: #666;
  border-color: #333;
}

/*** Page Loading ***/

.cloudonaut-image {
  filter: invert(86%);
  opacity: 0.4;
}

.fake-topbar, .fake-metadata, .fake-panel-header {
  background-color: #222;
  border-bottom-color: #333;
}

.fake-panel-1 {
  border-right-color: #333;
}

/** Preview Loading **/
.splash {
  background-color: #222;
}

.preview-container {
  background-color: #222;
}

/***** Explore Page - latexbase.com/explore *****/

.app {
  background-color: #222;
}

/* Disabled Mode */
.editor-disabled-mode {
  background-color: #222a; /* rgba(255, 255, 255, 0.4); */
}

/* 'Clone document' button */
.btn-info {
  color: #aaa;
  border-color: #3B444B; /* from latexcolor.com */
  background-color: #333399; /* from latexcolor.com */
}

/* 'Editing this document' on clicking Eye on top-right */
.standard-modal, .select-image-modal {
  background-color: #222;
}

/***** Pricing Page - latexbase.com/static/pricing *****/

/** Table **/

.pricing-cell {
  border-left-color: #333;
  border-top-color: #333;
}

.pricing-cell-invisible {
  border-bottom-color: #333 !important;
  border-left-color: #333 !important;
}

.pricing-cell:last-child {
  border-right-color: #333;
}

.pricing-row:last-child > .pricing-cell {
  border-bottom-color: #333;
}

/* 'Premium' column */
.background-highlight {
  background-color: #282828;
}

}
