css:
@-moz-document domain(github.com) {
.highlight pre,
.blob-wrapper,
.blob-expanded .blob-code,
.blob-code-context,
.blob-num-context,
.blob-expanded .blob-num {
    background-color: #0e354b !important;
    color: #e1efff !important;
}
.blob-code-inner.highlighted, .js-file-line-container .bg-white {
    background-color: #394E6E !important;
}
.bg-yellow-light {
    background-color: #2858b1 !important;
}
.blob-code-inner,
.blob-num,
.highlight pre,
.blob-wrapper {
    font-family: "Operator Mono SSm Lig" !important;
    color: #e1efff !important;
    font-size: 16px;
    line-height: 135% !important;
}
.blob-num,
.blob-expanded .blob-num {
    color: #aaa !important;
}
.blob-code-hunk,
.blob-num-hunk,
.blob-num-expandable,
.blob-code-expandable {
    background-color: #1E88E5;
}
.blob-code-addition,
.blob-num-addition {
    background-color: #005500;
}
.blob-code-deletion,
.blob-num-deletion {
    background-color: #550000;
}
.pl-c,
.pl-e {
    font-style: italic;
}
.pl-k {
    color: #FF9d00;
}
.pl-kos {
    color: #FFC600;
}
.pl-pds {
    color: #e1efff;
}
.pl-pse {
    color: #e1efff;
}
.pl-smi {
    color: #e1efff;
}
.pl-v {
    color: #e1efff;
}
.pl-en {
    color: #ffc600;
}
.pl-c1 {
    color: #9EFFFF;
}
.pl-s {
    color: #a5ff90;
}
.pl-s .pl-pds {
    color: #3ad900;
}
.pl-s1 {
    color: #e1efff !important;
}
.pl-s1.x {
    color: #666 !important;
}
.pl-s1 .pl-pse {
    color: #ffee80;
}
.pl-ent {
    color: #ffc600;
}
.pl-c {
    color: #0088ff;
}
.pl-e {
    color: #3ad900;
}
.pl-sr {
    color: #a5ff90;
}
.pl-sr .pl-cce {
    color: #ff628c !important;
}
.pl-sr .pl-pds {
    color: #92fc79 !important;
}
}
