css:
/* i really want this to be global */

/* ! Wyłącza górny baner */
.userProfileHeader .userPreview {
	display: none;
}

/* ! Data nad gwiazdkami off */
.userEventSummary__user {
    display: none;
}

/* ! Gatunek filmu itp. */
.filmPreview .filmPreview__boxes {
    display: none;
}
.filmPreview .filmPreview__details {
    display: none;
}
.userEvent .socialMenu {
    display: none;
}
.userEventSummary .userRate {
    margin: 0px;
}

/* ! Ukrywa oceny aktorów po prawej */
.myVoteBox__rightCol {
	display: none;
}

/* ! Wyskosc obrazka filmu */
.filmPoster.filmPoster--normal .filmPoster__link, .filmPoster.filmPoster--normal .filmPoster__link:after {
    height: auto;
    width: auto;
}
.filmPoster.filmPoster--normal .filmPoster__image {
	width: auto;
}
.filmPoster .filmPoster__image {
    height: 80px;
    width: auto;
}
.filmPoster .filmPoster__link, .filmPoster .filmPoster__link:after {
    height: 80px;
    width: auto;
}
.userEvent .rolePreview__photoHorizontal {
	width: 80px;	
	height: 80px;
}

/* ! Wyskosc boxow na Moj Filmweb */
.filmPreview .filmPreview__card {
	min-height: 0px;
}
.filmPreview .filmPreview__poster {
	height: auto;
}

/* ! Wyskosc boxow na Moj Filmweb */
.filmPreview .filmPreview__card {
	min-height: 0px;
}
.filmPreview .filmPreview__poster {
	height: auto;
}

/* ! Wyskosc boxow na O mnie Filmweb */
.posterInfoBox .filmPoster .filmPoster__link, .posterInfoBox .filmPoster .filmPoster__link:after {
	width: auto;
	height: auto;
}
.posterInfoBox .filmPoster .filmPoster__link, .posterInfoBox .filmPoster .filmPoster__link:after {
	width: auto;
	height: auto;	
}
/* ! Tło pod obrazkiem */
.filmPoster .filmPoster__link {
	background: none;
}

/* ! 1 strona */
.filmPoster.filmPoster--opacityImage .filmPoster__link .filmPoster__image {
	opacity: 1;
	text-align: center;
}
.filmPoster.filmPoster--big .filmPoster__image {
	width: auto;
}

	
/* ! top 500 */	
.filmPoster.filmPoster--tiny .filmPoster__image {
	width: auto;
}	
.filmPoster.filmPoster--tiny .filmPoster__link, .filmPoster.filmPoster--tiny .filmPoster__link:after {
	width: auto;
	height: auto;
}
