css:
@-moz-document domain("trello.com") {
  body {
    line-height: 1.5em;
  }
  h1 {
    margin: 1em 0;
  }
  p {
    margin-bottom: 1em;
  }

  .badge.is-complete {
    max-height: 1em;
  }

  .comment-box-input {
    padding: 1.5em;
  }
  .window {
    width: auto;
    padding: 2em;
  }
  .window.mod-notifications.mod-cards {
    padding: 0;
  }
  .window-module {
    margin-bottom: 2em;
  }
  .list-card-labels > span.card-label {
    min-width: auto !important;
    max-height: 1em !important;
    padding-bottom: 2px !important;
  }
  .list-wrapper {
    width: auto;
  }
  .list-card {
    max-width: 22em;
  }
  .list-card.is-covered .list-card-cover {
    max-height: 10em;
  }
  .list-card-details {
    padding: 0.75em 0.75em 0;
  }
  .list-card-members .member {
    margin: 0 0 4px 2px;
  }
  .list-card .member {
    width: 1.75em;
    height: 1.75em;
  }
  .list-card .member-avatar {
    height: 1.75em;
    width: 1.75em;
    border-radius: calc(1.75em / 2);
  }
  .dialog-close-button {
    font-size: 2em;
    top: 0.5em;
    right: 0.5em;
  }
  .checklist-completed-text {
    display: none;
  }
  .description-content {
    line-height: 1.75;
  }
  .action-comment .current-comment {
    padding: 1em;
  }
  .checklist-item {
    margin-bottom: -0.25em;
  }
  .card-detail-window .window-main-col {
    margin: 0 2em 0 0;
  }

  .js-copy-card .icon-card:before {
    content: "\f24d";
    font-family: "Font Awesome 5 Free";
    font-weight: 900;
  }
}
