css:
@-moz-document domain(www.coinigy.com) {
/* Hide exchanges */
#exchange_VWOX, #exchange_GOLD, #exchange_ROCK, #exchange_QCX, #exchange_PAYM, #exchange_OKEX, #exchange_OKFT, #exchange_MERC, #exchange_LIVE, #exchange_LAKE, #exchange_KRKN, #exchange_KBIT, #exchange_ITBT, #exchange_GDAX, #exchange_GMNI, #exchange_GATE, #exchange_EXMO, #exchange_CBNK, #exchange_CONE, #exchange_MATE, #exchange_CCJP, #exchange_CXIO, #exchange_CCEX, #exchange_BTCM, #exchange_BLEU, #exchange_BITS, #exchange_BMEX, #exchange_BTHM, #exchange_FLYR, #exchange_BITF, #exchange_BIND, #exchange_BXTH {
	display: none !important;
}

/* Show only these exchanges */
#exchange_BIN, #exchange_PLNX, #exchange_BTRX, #exchange_CPIA, #exchange_YOBT, #exchange_HITB, #exchange_KUCN, #exchange_LIVE, #exchange_LIQU {
    display: block;
}


/* Smaller markets */
.list-group .list-group-item {
    padding: 7px 6px 6px 15px;
}

/* tighter rows */
.table tbody tr td {
    padding: 3px !important;    
}

#balance_table .table-striped > tbody > tr:nth-child(2n+1) > td {
    background: rgba(147, 143, 143, .1);
}

/* Hide dumb shit */
#blockchain_chart_button {
    display: none;
}

#buy_button_chart, #sell_button_chart {
    display: none;
}

/* TA & chart laytouts */
.save-load-buttons, .header-group-indicators, .header-group-compare{
    display: none !important;
}

/* Apps */
.main-nav-items .dropdown {
	display: none;	
}

/* useless chart */
#series_tab {
    display: none;
}

/* Markets & Balances tab */
#section_1 {
	display: none;
}

/* chart type in depth */
#section_9 #charttype_tab {
    display: none !important;
}

/* Useless information */
#economy_li {
    display: none;
}

/* Dont need to see all data when in a specific coin */
#ordersRadio_1_a, #ordersRadio_1_b, #ordersRadio_2_a, #ordersRadio_2_b {
    display: none;
}
#alertsRadio_1_a, #alertsRadio_2_a, #alertsRadio_1_b, #alertsRadio_2_b {
    display: none
}

/* Copyright etc */
.page-footer-wrap table td:first-child {
    display: none;
}

/* Charting Powered By */
.data-by-cgy {
	display: none;
}


/* Darken */
	.tv-side-toolbar, body, .header-chart-panel {
        background: #131721 !important;
    }

    .tv-close-panel.top, .tv-close-panel.left {
        background-color: #131721;   
    }

    .header-chart-panel {
        border: none;
    }

    .header-chart-panel .button, .favored-list-container span.submenu, .favored-list-container span.submenu, .favored-list-container .quick span:first-child, .header-chart-panel .favored-list-container span {
        border: 1px solid #313947;
        background: #131721;
    }

    .header-chart-panel .button.disabled {
        border: 1px solid #313947 !important;
        background: #131721 !important;
    }

    .chart-controls-bar {
       background: #131721;
    }

    .chart-page .chart-container, .header-chart-panel, #library-container, .tv-side-toolbar {
        border: 1px solid #313947 !important; ;   
    }
}
