css:
/*v09-03-12*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fmylife.com") , domain("viedemerde.fr")  {

body                                        { background: #181818 !important; color: #999 !important; }

a                                           { color: #BBB !important; text-decoration: none !important; }
a:hover                                     { color: white !important; }



div#wrapper                                 { width: 726px !important; }

div#submit,
div#menu h1,
div#wrapper div.post,
div#droite div.box,
div#wrapper div.boutons > div               { background: #222 !important; margin-bottom: 10px !important; }

div#wrapper div.post:hover                  { background: #292929 !important; } 

div#wrapper div.post:hover p a              { color: white !important; }

div#submit:hover,
div#menu h1:hover,
div#wrapper div.boutons > div:hover,
div#droite div.box:hover                    { background: #272727 !important; }  


div#submit,
div#menu h1,
div#wrapper div.boutons > div,
div#droite div.box                          { border: 1px solid #444 !important; }


div#droite div.titretop,
div#droite h4                               { text-decoration: underline !important; color: #CCC !important; }


div#content input[type="text"],
div#content input[type="password"],
div#content input[type="submit"],
div#content select,
div#content textarea {
  -moz-appearance: none !important;
  background: #1C1C1C !important;
  border: 1px solid #555 !important;
  color: #AAA !important;
  padding: 0 !important;
}

div#content input[type="text"]:focus,
div#content input[type="password"]:focus,
div#content textarea:focus                  { color: white !important; }

div#content input[type="submit"]:hover      { background: #333 !important; color: white !important; }

div#content input[type="text"],
div#content input[type="password"]          { height: 18px !important; }

div#content input[type="submit"]            { padding: 3px 15px !important; }

div#content select                          { height: 20px !important; }

div#content textarea                        { padding: 5px !important; }

div#haut input#submit_srch                  { margin-top: 1px !important; }

div#droite div.loginform                    { text-align: right !important; }

div#content form textarea                   { width: 870px !important; }



div#haut div.twitter,
div#haut div.facebook
{ display: none !important; }

}

/* Some vdm.fr specific stuff */
@-moz-document domain("fmylife.com") , domain("viedemerde.fr")  {

div#content div#submit                      { -moz-border-radius: 8px !important; margin: 30px 0 10px !important; padding: 10px !important; }

div#content div#submit form div.input_cat,
div#content div#submit form div.ok          { margin-right: 0 !important; }

div#content div#submit form textarea        { width: 866px !important; }

div#wrapper div.carreinfos,
div#wrapper div.carreinfos li.grey          { background: #222 !important; }

div#wrapper div.carreinfos li[class*="border"] { border-color: #444 !important; }



div#haut div.premiere { display: none !important; }

}
