css:
/*Flat White Whatsapp. Copyright: Userstyles.org user: Bootis*/


@-moz-document domain("web.whatsapp.com")
{
html[dir]   
    
 /*Make Whatsapp full width*/

.app
  {
    width: 100%;
    height: 100%;
    top: 0px;
  }
 
.pane-chat
  {
    width: 100%;
  }

/*Hide video previews*/
 .video-thumb
  {
    width: 1px !important;
    height: 1px !important;
    
  }

 /*Main chat window background full color, changes color behind doodles*/
._1GX8_ {
    background-color: white !important; 

}

/*Main chat window background bottom layer, prevents doodle design from appearing*/
html[dir] .YUoyu {
    background: white !important;
}



/*Small image on the beginning of sent chat thread*/
.message-out .tail-container {
    background-image: none;
}



/*Chat list top message such as "Get Nitified of New Messages"*/
.m6ZEb{
    display:none;
}

/*Left chat panel list: Chat titles*/
._25Ooe {
    color: black;
    opacity: 0.3;
}

/*Left chat panel list: Unselected chats last message sent-received*/
._1AwDx{
    color: black;
    opacity: 0.3;
}

/*Selected chat last message sent'received*/
._2EXPL._1f1zm ._1AwDx {
   color: black;
    opacity: 0.3;
}

/*Person or group image on list of chats*/
._1WliW img{
	opacity:0.04 !important;
}

/*Turn on or off person or group generic image on list of chats*/
._3ZW2E{
    display:none;
}

/*Color of box surrounding main chat writing field*/
html[dir] ._3oju3 {
    background-color: white;
    padding: 10px 19px;
}

/*"Type a message" message on main writing field*/
._3F6QL ._39LWd {
    color: black;
    opacity: 0.3;
}

/*Current written message not yet sent*/
._2S1VP.copyable-text.selectable-text{
	color: black;
    opacity: 0.3;
}

/*Current open chat title on main chat window*/
._2zCDG{
	color: black;
    opacity: 0.3;
}


/*Emojis on chat list titles*/
._1wjpf img{
	opacity:0.01;
}

/*Color of message sent bubbles*/
html[dir] ._1sGGp._2nFG1,html[dir] .message-out {
    background-color: white;
}


/*All chat texts on main chat window*/
._3QqKs, ._3_7SH {
    color: black;
    opacity: 0.3;
}


/*Opacity of location and active links on sent messages*/
a {
    color: black;
    opacity: 0.9;
}

/*Opacity of emojis sent in chat messages*/
img._3lZNp {
    color: red;
    opacity: 0.09;
}

/*Opacity or images sent and received as chat messages*/
img._1JVSX {
    color: red;
    opacity: 0.01;
}

/*Opacity of emojis received in chat messages*/
.emoji{
    opacity:0.01;
}
}
