/* ------– */


[title~="@today"] {
    color: #796686 !important;
    padding: 1px 3px !important;
    font-family: menlo;
    border: 1px solid #796686 !important;
    border-radius: 7px !important;
	transition: border .3s ease-in-out;
}

[title~="@today"] .contentTagText {
    text-decoration: none !important;
}

[title~="@today"] .contentMatch {
    background-color: transparent !important;
    color: #796686 !important;
}
[title~="@today"]:hover {
    background-color: transparent !important;
    text-decoration: none;
	border: 1px dashed #796686 !important;
	transition: border .3s ease-in-out;
	transition-delay: 3s;
}


