@-webkit-document domain("532movie.bnu.edu.cn") {
/**=========================首页=========================**/
	body {
		background: -webkit-Radial-gradient(#302a30, #272b38) !important;
		color: #ffffff !important;
		font-weight: 500 !important;
											
	}
	:hover {
		transition: none !important;
	}
	/**首页 导航条**/
	.navbar-inverse .navbar-inner/**导航条整体背景**/
		{
		background: -webkit-Radial-gradient(#302a30, #272b38) !important;
		border: none !important;
		letter-spacing: 2px !important;																			
	}
	.index_header .navbar-inner ul li a,
	.index_header .navbar-inner .brand/**导航条选中边框**/
		{
		border-radius: 25px !important;
	}
	.index_header .navbar-inner ul li a:hover,
	.index_header .navbar-inner .brand:hover/**导航条悬浮颜色**/
		{
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		border-radius: 25px !important;
		transition: background 0.6s !important;
	}
	.form-search .input-append .btn/**搜索按钮背景**/
		{
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		border: none !important;
	}
	.form-search input,
	.form-inline input,
	.form-horizontal input,
	.form-search textarea,
	.form-inline textarea,
	.form-horizontal textarea,
	.form-search select,
	.form-inline select,
	.form-horizontal select,
	.form-search .help-inline,
	.form-inline .help-inline,
	.form-horizontal .help-inline,
	.form-search .uneditable-input,
	.form-inline .uneditable-input,
	.form-horizontal .uneditable-input,
	.form-search .input-prepend,
	.form-inline .input-prepend,
	.form-horizontal .input-prepend,
	.form-search .input-append,
	.form-inline .input-append,
	.form-horizontal .input-append {
		border: none !important;
											
	}
	.dropdown-menu,
	.input-append .popover,
	.input-prepend .popover/**搜索栏 下拉**/
		{
		border-bottom-left-radius: 25px !important;
		border-bottom-right-radius: 25px !important;
		background: rgba(0, 0, 0, 0.71) !important;
	}
	.input-append .active,
	.input-prepend .active {
		border-radius: 25px !important;
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		color: #fff !important;
	}
	/**首页 悬浮窗口**/
	.popover {
		background: -webkit-Radial-gradient(#302a30, #272b38) !important;
		letter-spacing: 1px !important;
	}
	/**首页 幻灯片 myCarousel**/
	#myCarousel .bg {
		background: none !important;
	}
	.carousel-inner > .item > img,
	/**幻灯片图片停留变换**/
	.carousel-inner > .item > a > img:hover {
		opacity: 0.5 !important;
		transition: opacity 0.6s !important;
	}
	.carousel-inner :hover/**幻灯片文字停留变换**/
		{
		transition: 
																					0.6s !important;
		transition: letter-spacing 0.3s !important;
		text-shadow: 3px 3px 3px #dc2f6d;
		letter-spacing: 5px !important;
	}
	#myCarousel .carousel-control/**幻灯片切换按钮渐变也有问题，不知道怎么解决**/
		{
		display: inline-block;
		color: #dc2e6d;
		background-image: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		-webkit-background-clip: text !important;
		-webkit-text-fill-color: transparent !important;
	}
	/**首页 标题**/
	.index_thumbnails h3/**标题字体**/
		{
		color: #fff !important;
		background: none !important;
		border-bottom: 4px solid !important;
		border-image: -webkit-Radial-gradient(
		#6180cd, #04a9f5, #dc2e6d) 3  3 3 ;
	}
	.index_thumbnails h3 ul li a,
	.index_thumbnails h3 span/**标题侧边选项字体**/
		{
		color: #fff !important;
	}
	.nav > li > a:hover,
	.nav > li > a:focus/**标题侧边选项背景**/
		{
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		border-radius: 20px !important;
	}
	#Popular/**本周热门下拉**/
		{
		background: -webkit-linear-gradient(left, #6180cd, #dc2e6d)!important;
		color: #fff !important;
		border-top-left-radius: 25px !important;
		border-bottom-left-radius: 25px !important;
	}
	p/**段落**/
		{
		color: #fff !important;
		letter-spacing: 1px !important;
		font-size: 14px !important;
	}
	.footer a {
		display: inline-block;
		color: #dc2e6d;
		background-image: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		-webkit-background-clip: text;
		-webkit-text-fill-color: transparent;
	}
	.thumbnail/**图片背景，周围渐变边框**/
		{
		background: -webkit-Radial-gradient(left top, #6180cd, #04a9f5, #dc2e6d)!important;
	}
	/**因背景使用的是图片，暂时不会修改   #scrollUp{}**/
	/** ===================分类页=======================**/
	/**导航栏**/
	.navbar-inverse .nav > li > a:hover,
	.navbar-inner .brand:hover/**532brand**/
		{
		background: -webkit-linear-gradient(left, #6180cd, #dc2e6d)!important;
																					
	}
	.navbar-inverse .brand,
	.navbar-inverse .nav > li > a {
		color: #fff !important;
		
	}
	.navbar-inverse .nav .active > a,
	.navbar-inverse .nav .active > a:hover,
	.navbar-inverse .nav .active > a:focus/**导航栏选中边框**/
		{
		border-radius: 25px !important;
	}
	.navbar-inverse .brand {
		border-radius: 25px !important;
	}
	.open > .dropdown-menu {
		background: rgba(0, 0, 0, 0.43)!important;
	}
	/**更多及下拉**/
	.dropdown-menu > li > a {
		color: #fff !important;
		border-radius: 25px !important;
		border: 0px !important;
	}
	.mydropdown .dropdown-menu > li > a:hover,
	.mydropdown .dropdown-menu > li > a:focus,
	.mydropdown .dropdown-submenu:hover > a,
	.mydropdown .dropdown-submenu:focus > a {
		background: -webkit-linear-gradient(left top, #6180cd, #04a9f5, #dc2e6d)!important;
	}
	.input-append .popover,
	.input-prepend .popover {
		background: -webkit-linear-gradient(left top, #6180cd, #04a9f5, #dc2e6d)!important;
	}
	/**分类**/
	.pre-box {
		background: none !important;
		border-image: -webkit-linear-gradient(left top, #6180cd, #04a9f5, #dc2e6d) 30 30;
		border-bottom: 5px solid !important;
		border-top: 0px solid !important;
		border-left: 0px solid !important;
		border-right: 0px solid !important;
	}
	.pre-tit {
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		border-radius: 25px !important;
		margin-top: 3px !important;
	}
	.search-letter a {
		color: #fff !important;
	}
	.search-letter a:hover {
		transition: font-size .5s !important;
		display: inline-block;
		color: #dc2e6d;
		font-size: 15px !important;
		background-image: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		-webkit-background-clip: text;
		-webkit-text-fill-color: transparent;
	}
	.search-letter a.Letter {
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
	}
	.pre-area .Year,
	.pre-area .Letter,
	.pre-area .Area,
	.pre-area .Class,
	.pre-area .Order {
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
	}
	.pre-area a:hover {
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
	}
	a.color1,
	a.color2,
	a.color3,
	a.color4,
	a.color5,
	a.color6,
	a.color7,
	a.color8,
	a.color9,
	a.color10,
	a.color11,
	a.color12,
	a.color13,
	a.color14,
	a.color0 {
		background: none !important;
		border-radius: 25px !important;
	}
	/**页码**/
	.page {
		font-size: 13px !important;
		letter-spacing: 2px !important;
	}
	.page a {
		border: 1px solid #d3d3d3;
		color: #fff;
	}
	.page span/**选中**/
		{
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		color: #fff !important;
	}
	.page a:hover/**悬停**/
		{
		color: #dc2e6d;
		background-image: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		-webkit-background-clip: text;
		-webkit-text-fill-color: transparent;
	}
	/** ===================详情页=======================**/
	.img-polaroid/**图片**/
		{
		background: -webkit-linear-gradient(left top, #6180cd, #04a9f5, #dc2e6d)!important;
		border: none !important;
	}
	.badge-important/**重要排序**/
		{
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		border-radius: 25px !important;
	}
	.table-hover tbody tr:hover {
		color: #dc2e6d;
		background-image: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		-webkit-background-clip: text;
		-webkit-text-fill-color: transparent;
	}
	.label/**标签**/
		{
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		font-weight: normal;
		font-size: 12px !important;
		letter-spacing: 2px !important;
	}
	/**侧边排名**/
	.rank_table {
		border: none !important; 
	}
	
	.rank_table a {
			color: #fff !important;
		}
	.table-hover tbody tr:hover > td,
	.table-hover tbody tr:hover > th {
		background: transparent;
	}
	.well {
		background: transparent !important;
		border-image: -webkit-linear-gradient(left top, #6180cd, #04a9f5, #dc2e6d) 30 30;
		border-top: none !important;
	}
	.nav-pills > li > a {
		color: #fff !important;
	}
	/**发表评论**/
	.control-group,
	.ke-container-simple,
	.ke-toolbar {
		background: none!important;
		border-image: -webkit-linear-gradient(left top, #6180cd, #04a9f5, #dc2e6d) 30 30;
		border-top: none !important;
		border-left: none !important;
		border-right: none !important;
	}
	.ke-outline {
		border: none !important;
	}
	.ke-container-simple .ke-statusbar {
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
	}
	.commlist ul li {
		border-image: -webkit-linear-gradient(left top, #6180cd, #04a9f5, #dc2e6d) 30 30;
	}
	.btn-primary {
		background: transparent !important;
		border: none !important;
	}
	.btn-primary:hover,
	.btn-primary:focus,
	.btn-primary:active,
	.btn-primary.active,
	.btn-primary.disabled,
	.btn-primary[disabled] {
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
	}
	
	.alert-info{
	color:#fff!important;
										background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
											border:none !important;
											letter-spacing: 2px !important;
											border-radius:25px !important;
											text-shadow:none !important;
                    }

/**立即观看**/
	.btn-success {
		color: #fff!important;
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		border: none !important;
	}
	.btn-success:hover,
	.btn-success:focus,
	.btn-success:active,
	.btn-success.active,
	.btn-success.disabled,
	.btn-success[disabled] {
		color: #fff!important;
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
		border: none !important;
	}


	.btn:hover, .btn:focus{
	text-decoration: none;
background-position: 0 -15px;
	}
/**展开列表**/
	button#plMore.btn.btn-block {
	color: #fff!important;
		background: -webkit-linear-gradient(left top, #6180cd, #dc2e6d)!important;
											border:none !important;
											text-shadow:none !important;
	}
	
	/**===============播放页================**/
	.play_well {
		background: transparent !important;
	}
.html5-play-progress, .dplayer-played {
       height: 12px !important;
   	   background: url("data:image/png;base64,") repeat-x !important; 
}

       

.dplayer-thumb{ background: url("data:image/gif;base64,R0lGO	") !important;
   width: 34px !important;
   height: 21px !important;
   border: none !important;
   margin-left: -18px !important;
   margin-top: -15px !important;
   /*[[show_nyan]]*/
    }




 		.dplayer-bar .dplayer-loaded {
                 height: 12px !important;    
 				  background:  url("data:image/gif;base64,") !important;
		}
}
