css:
@media print {
    footer, .page__header, .form__label, .form__select, .form__date-button, .heading, .page__content--membersarea, .horizontal-rule, .calendar-card__status, .toolbar-spacer, .gympage-footer, .button--back {
        display: none;
        opacity: 0;
    }
    .page__content {
        padding: 0;
        padding-top: 70px;
    }
    .page__content:before {
        content: " ";
        display: block;
        position: absolute;
        top: 0;
        left: 0;
        width: 140px;
        height: 65px;
        background-image: url('data:image/png;base64,');
    }
    .container {
        margin: 0;
        padding: 0;
    }
    .calendar .calendar-column {
        display: none;
    }
    .calendar .calendar-column:first-child {
        display: block;
    }
    .calendar--list .calendar-column {
        width: 100%;
        padding: 0;
        text-align: left;
        clear: both;
    }
    .calendar--list .calendar-column__title {
        display: block;
        text-align: center;
    }
    .calendar--list .calendar-column__head {
        display: block;
        clear: both;
        color: white;
    }
    .calendar--list .calendar-card {
        display: block;
        position: relative;
        width: 100%;
        border: none;
        border-bottom: 4px solid white;
        padding: 0;
    }
    .calendar--list .calendar-card:before {
        display: none;
    }
    .calendar--list .calendar-card__time,
    .calendar--list .calendar-card__entry-time,
    .calendar--list .calendar-card__duration,
    .calendar--list .calendar-card__class,
    .calendar--list .calendar-card__instructor,
    .calendar--list .calendar-card__location,
    .calendar--list .calendar-card__date,
    .calendar--list .calendar-card__gym,
    .calendar--list .calendar-card__waiting-list {
        position: relative;
        float: left;
        text-align: left;
        bottom: 0;
        margin: 0;
        border: 0;
        padding: 10px;
        height: auto;
    }
    .calendar--list .calendar-card__buttons {
        position: relative;
        float: left;
        text-align: left;
    }
    .calendar--list .calendar-card__class {
        width: 35%;
        font-size: 14px;
    }
    .calendar--list .calendar-card__instructor,
    .calendar--list .calendar-card__gym {
        width: 25%;
    }
    .calendar--list .calendar-card__location {
        width: 20%;
    }
    .calendar--list .calendar-card__date {
        width: 13%;
    }
    .calendar--list .calendar-card__duration {
        width: 13%;
    }
    .calendar--list .calendar-card__time {
        width: 7%;
        font-size: 14px;
    }
    .calendar--list .calendar-card__entry-time {
        width: 17%;
        font-size: 14px;
    }
    .calendar--list .calendar-card--summary .calendar-card__instructor {
        width: 15%;
    }
    .calendar--list .calendar-card--summary .calendar-card__location {
        width: 10%;
    }
    .calendar--list .calendar-card__waiting-list {
        width: 25%;
    }
    .calendar--list .calendar-card--head .calendar-card__time,
    .calendar--list .calendar-card--head .calendar-card__entry-time,
    .calendar--list .calendar-card--head .calendar-card__date {
        color: white;
    }
    .calendar--list .calendar-card--head {
        background-color: #004e51;
        color: #fff;
        cursor: default;
        font-weight: 700;
        font-size: 14px;
    }
}
