css:
@-moz-document domain("theoldreader.com"){

.folders-list {
    margin-top: 10px;
}

.nav-list {
    padding: 0 5px;
}

.nav-list .badge {
    padding: 0
    ;
}

.nav-list li {
    padding: 2px 0 0 30px;
}

.nav-list li.nav-header {
    margin: 0;
}

.nav-list li.nav-header a {
    padding: 0;
}

.post .content .content-body {
    overflow: hidden;
    width: 640px;
}

.posts {
  margin: 0;
    padding: 0;

}

.post {
    margin-bottom:3px;
}
.sidebar-nav a {
    color: #666;
    text-decoration: none;
}

.well {
    background: #ffffff;
}

a.brand {
    display: none;
}

a.label.label-social {
    display: none;
    color: #ff2400;
}

a.social {
    display: none;
    color: #333333;
}

body {
    font-family: Arial,sans-serif;
  background-color:#dddddd;
}

div.content-body {
    font-size: 12.5px;
}

div.content-body blockquote {
    background-color: #ffffff;
    border-style: none;
}

div.content-body blockquote p {
    line-height: 17px;
}

div.content-body h3 {
    font-size: 15px;
}

div.content-body h4 {
    font-size: 12.5px;
}

div.content-body p a {
    color: #1155CC;
    text-decoration: underline;
}

div.content-body p code {
    background-color: #ffffff;
    border-style: none;
}

div.relations-info h3 {
    display: none;
}

div.section {
    display: none;
}

div.section.plugins {
    display: none;
}

div.span5 {
    display: none;
}

div.span5 h3 {
    display: none;
}

div.unteleported {
    display: none;
}

div.well {
    color: #DDDDDD;
}

div.well.clearfix.post {
    border-color: #cccccc;
    border-style: solid;
    border-width: 1px;
}

div.well.clearfix.post h3 a {
    color: #15c;
}

div.well.sidebar-nav {
    border-style: none;
    color: #ffffff;
}

i.favicon {
    background-image: url('https://dl.dropbox.com/u/3948038/rss-icon.png');
}

i.icon-chevron-down {
    color: #666666;
    font-size: 16px;
}

i.icon-chevron-right {
    color: #666666;
    font-size: 13px;
}

i.icon-heart {
    display: none;
}

i.icon-home {
    display: none;
}

i.icon-list {
    display: none;
}

i.icon-share-alt {
    display: none;
}

i.icon-signal {
    display: none;
}

li.nav-header.dropdown {
    padding: 0;
}

li.nav-header.dropdown a {
    text-decoration: none;
}

li.nav-header.dropdown a span {
    color: #000000;
    font-size: 13px;
    text-transform: none;
}

li.sort-item {
    padding-left: 30px
    ;
}

li.sort-item a {
    color: #000;
}

li.sort-item.active > .favicon {
    background-image:
url('https://dl.dropbox.com/u/3948038/rss-icon-active.png');
    border-left: 3px solid #DD4B39;
    margin-left: -17px;
    padding-left: 28px;
}

li.sort-item.active a strong {
    color: #D14836;
    font-style: italic;
    font-weight: bold;
    /* margin-left: -14px; */
    background-color:#FFFFDD;
    padding-left: 0;
}

span.badge {
    background-color: #ffffff;
    font-weight: normal;
}

span.badge.badge-info {
    background-color: #ffffff;
    color: #333333;
    font-size: 12px;
    font-weight: bold;
}

span.badge.badge-info:after {
    content: ")";
}

span.badge.badge-info:before {
    content: "(";
}

span.badge:after {
    content: ")";
}

span.badge:before {
    content: "(";
}

span.icon.hidden-tablet.hidden-phone {
    display: none;
}

span.label.label-transparent.pull-right {
    color: #333;
    font-size: 14px
;
}

ul li a {
    color: #ff2400;
    text-decoration: underline;
}

div.navbar {
    display: none;
}
.reader .container-fluid{
 top:0px;
 background-color : #333333;
}
.nav-list li.social{
   display:none;
}
.page-header{
   background-color: #dddddd;
}
.reader .floating {
   padding:0;
}
.slide {
top: 80px;

}

.reader .sidebar-fixed-top .slide {
    background-color: white;
}

.banner-inner {
     display:none;
}

.reader .banner {
    height: 0px;
}

.reader .sidebar-fixed-top.banner_user {
  height: 1000px;
}

.navbar-default .navbar-nav>li>a:hover, .navbar-default .navbar-nav>li>a:focus {
    color: #eeeeee;
    background-color: transparent;
}
#social {
    color: #eeeeee;
}
.navbar-default .navbar-nav>li>a {
	color : #ff2400;
    text-decoration: none;
}

}
