css:
@-moz-document domain("forum.fantabobworld.com") {
    /* Fond général */
body.styliumBoxed2 {
    background: url(https://www.pixenli.com/images/1502/1502384982048291400.jpg) center fixed;
}

body > div.backstretch {
    visibility: hidden;
}

footer {
    background: url(https://www.pixenli.com/images/1502/1502384982048291400.jpg) center fixed;
}
    
    /* Avatar utlisateur */
.sidebar .section.visitorPanel .visitorText {
    background: url(https://www.pixenli.com/images/1502/1502388989059243200.jpg) center fixed;
}
    
.sidebar .visitorPanel .avatar img {
    -moz-box-shadow: 0px 0px 10px 0px #ffffff;
    -webkit-box-shadow: 0px 0px 10px 0px #ffffff;
    -o-box-shadow: 0px 0px 10px 0px #ffffff;
    box-shadow: 0px 0px 10px 0px #ffffff;
}

    /* Taille du footer */
.footerLegal {
	width: 99vw;
  	margin-left: calc(-50vw + 50%);
}

.styliumAdvancedFooterContainer {
	visibility: hidden;
}

    /* Virage du logo*/
#logoBlock  {
    background: url(https://www.pixenli.com/images/1502/1502384982048291400.jpg) center fixed;
}

#logoBlock img {
    visibility: hidden;
}

.node .forumNodeInfo .nodeIcon,	.node .categoryForumNodeInfo .nodeIcon {
	background-image: url(https://www.pixenli.com/images/1502/1502388076006552200.png);
}

.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
	background-image: url(https://www.pixenli.com/images/1502/1502388076006552200.png);
}

.node .pageNodeInfo .nodeIcon {
	background-image: url(https://www.pixenli.com/images/1502/1502388076006552200.png);
}

.node .linkNodeInfo .nodeIcon {
	background-image: url(https://www.pixenli.com/images/1502/1502388076006552200.png);
}

#styliumSetting .styliumPrimaryColor, .button.primary, .attachment .thumbnail, .PageNav a.currentPage, .PageNav a:hover, .PageNav a:focus, a.callToAction span, .sidebar .visitorPanel .stats, .xenOverlay .section .heading, .xenOverlay .sectionMain .heading, .BRML_menuHidden, .navTabs .navTab:first-child .navLink, #QuickSearch input.button.primary, #QuickSearch input.button.primary.BRML_searchButton, .navTabs .navLink .itemCount, .heading, .xenForm .formHeader, .node .subForumList li .nodeTitle a:hover, .BRML_backgroundBeforeNav, .downloadButton, .navTabs .publicTabs .navTab .navLink:hover {
    background: url(https://www.pixenli.com/images/1502/1502388989059243200.jpg) center fixed;
}

#QuickSearch input.button.primary.BRML_searchButton {
    background: url(https://forum.fantabobworld.com/styles/brivium/stylium/molybdenum/extra/brivium-images.png) no-repeat center 10px rgba(0,0,0,.4);
}
    
    /* Couleurs */
.node .nodeLastPost .lastThreadMeta .username, .attachment .attachmentInfo dt, .attachment .attachmentInfo dd, .LikeText .username, .footer a:hover, .footer a:active, #styliumAdvancedFooter a:hover, .footerLegal a:hover, .navTabs .navTab .tabLinks a:hover, .navTabs .navTab .tabLinks a:focus, .blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label, #SignupButton .inner:hover {
    color: rgb(70, 148, 173);
}
    
.publicControls .item.control {
    color: rgb(70, 148, 173);
}
    
.primaryContent a{
    color: rgb(70, 148, 173);
}
    
.node .subForumList li .nodeTitle a {
    color: #587b81;
}
    
.textWithCount.subHeading .text {
    color: rgb(240,240,240);
}
  
.item.control::before {
    background-image: url(https://www.pixenli.com/image/KeSST9zI);
}
    
    .message .newIndicator {
        background-color: rgb(70, 148, 173);
    }
    
    /* Page perso */
.subHeading {
    background: url(https://www.pixenli.com/images/1502/1502384982048291400.jpg) center fixed;
}
    
}
