/*BODY*/
[dir=ltr] .Root__main-view {
	padding-top: 60px;
	padding-left: 0;
}
/*SIDEBAR*/
 .navBar-item, .navBar-group-header, .navBar-item .type, .entity-info .media-bd .entity-additional-info {
	font-weight: 400 !important;
}
.navBar {
    margin: 6px 0px 0px 0px;
    width: 100%;
	padding: 0px !important;
	text-align: center;
    outline: none;
}
.navBar, .navBar-expand{
    outline: none;
}
.SearchInputBox__input {
    color: rgba(0, 0, 0, 0.61);
}
.navBar-group {
    border-top: 0;
    display: inline-block;
}
.navBar-item:hover {
	transition: 0.3s;
	background-color: rgba(0, 0, 0, 0.05);
	border-radius: 50px;
}
.navBar-item--with-icon-left {
	padding: 0px 0px 2px 0px;
    font-size: 15px;
}
.navBar-item .collection-active-icon, .navBar-item .collection-icon, .navBar-item .home-active-icon, .navBar-item .home-icon, .navBar-item .search-active-icon, .navBar-item .search-icon {
