	top: 5px;
}
.navBar-link {
	margin: 4px 0px 0px 0px;
}
.recently-played {
	display: none;
}
.navBar-expand ul {
	display: inline-block;
}
.navBar-link--active:after {
    content: "";
    display: block;
    position: absolute;
    top: 0px;
    bottom: 0px;
    border-radius: 50px;
    width: 100%;
    background-color: rgba(52, 232, 149, 0.37);
}
/*--SEARCHBAR--*/
[dir=ltr] .SearchInputBox {
    top: 60px;
    right: 0;
    left: 0;
}
