/*--BUTTONS--*/
 .control-button {
	cursor: pointer;
	color: #52eba5;
}
.control-button:hover {
	color: #0000002b;
}
.tracklist-middle-align .icon-pause, .tracklist-middle-align .icon-play {
    cursor: pointer;
    transform: translateY(-15px);
}
.btn.btn-green, .btn.btn-green:focus, .btn.btn-green:hover, .control-button:hover:active {
	background-color: #34e895;
	cursor: pointer;
}
.player-controls__buttons {
	margin-bottom: 0px;
}
.player-controls__buttons {
	width: 150px;
}
.navBar-item .playing-icon {
	top: 14px;
}
/*--player--*/
.now-playing-bar-container {
	background-color: #ffffff;
	color: #000;
	position: fixed;
	width: 620px;
	left: 50%;
	bottom: 20px;
	border-radius: 2px;
	box-shadow: 0 1px 1px 1px rgba(0, 0, 0, 0.07);
	margin-left: -310px;
	z-index: 101;
}
.cover-art.shadow {
	border: 0px;
    border-radius: 60px;
    overflow: hidden;
    box-shadow: none;
}
.now-playing__cover-art {
	margin-right: 0px!important;
}
.track-info {
	margin: 10px;
}
.playback-bar {
	position: absolute;
	top: 0px;
	width: 100%;
}
.progress-bar__bg {
	background-color: rgba(0, 0, 0, 0.05);
	height: 1px;
	cursor: pointer;
}
.progress-bar {
	height: 1px;
	position: relative;
	width: 100%;
}
.progress-bar__fg {
	background-color: #34e895 !important;
	height: 1px;
	cursor: pointer;
}
.playback-bar__progress-time:first-child {
	left: 44%;
}
.playback-bar__progress-time {
    color: #34e895;
	position: absolute;
	top: 58px;
	left: 51%;
}
.track-info__name, .track-info__artists, .link-subtle, .user-widget .user-link, .mo-info-name {
	letter-spacing: 0em;
}
.navBar-group-header {
	letter-spacing: 0.1em;
}
/*COVER LOG*/
 .sessionInfo {
	padding-top: 4px;
	position: absolute;
	right: 65px;
	top: 1px;
	border-top: 0;
}
.avatar {
	width: 36px !important;
	height: 36px !important;
}
.user-widget {
	margin: 0px 0;
}
.UserWidget__user-link {
	display: none;
}
