css:
@-moz-document domain("www.hotukdeals.com"), domain("www.chollometro.com") {
.tGrid-row,
.subNavMenu-btn,
.navMenu-trigger,
.navMenu-trigger:hover,
.navMenu-trigger:focus,
.navDropDown-btn,
.btn--mode-primary,
.btn--mode-primary:focus,
.btn--mode-primary:hover,
.btn--mode-special,
.btn--mode-special:focus,
.btn--mode-special:hover,
.vote-box,
.btn--mode-boxSec,
.btn--mode-boxSec:focus,
.btn--mode-boxSec:hover,
.popover-content,
.input,
.inputBox,
.secretCode-codeBox,
.toolbar,
.voucher-code,
.voucher-teaser,
.btn--disabled,
.btn:disabled,
.card-close:disabled,
.subNavMenu-list.cept-sort-tabs-merchant-list,
.navDropDown-head,
.btn,
.bg--color-inverted,
.bg--color-brandPrimary {
    background-color: #38383d !important;
}

.subNavMenu-roundCounter-text,
.bg--color-greyPanel,
.subNav,
.bg--color-brandSecondaryShade,
.thread--type-list,
.card,
.text--overlay,
.threadItem,
.cept-thread-content,
.input--search,
.comments-marker-down,
.comments-marker-down::after,
.comments-marker-up,
.comments-marker-up::after,
.bg--em,
.thread--type-card,
.bg--color-brandPrimaryPale {
    background-color: #2a2a2e !important;
}

.nav,
.threadItem-metaRibbons,
.bg--main,
.voteBar,
.navDropDown-link:hover,
.conversationList-msg,
.notification-item,
.softMessages-item,
.comments--top-header {
    background-color: #4a4a4f !important;
}

.icon--gear,
.icon--filter,
.subNavMenu--menu.subNavMenu--selected .subNavMenu-roundCounter-text,
.subNavMenu-btn,
.subNav,
.subNavMenu-link--selected,
.subNavMenu,
.cept-breadcrumbsList .link,
.userProfile-title--sub,
.thread--type-list,
.thread-title,
.text--color-brandPrimary,
.thread-username,
.thread-link,
.thread-price,
.nav-link-text,
.cept-thread-content,
.plainSelect,
.btn--mode-boxSec,
.btn--mode-boxSec:focus,
.btn--mode-boxSec:hover,
.text--color-charcoal,
.text--color-blue,
.thread-userOptionLink,
.mute--text2,
.navMenu-link,
.text--color-navDropDownPrimary,
.formList-label,
.formList-static--em {
    color: #e3e3e3 !important;
}

.subNavMenu-btn,
.subNav,
.vote-box,
.btn--mode-boxSec,
.btn--mode-boxSec:focus,
.btn--mode-boxSec:hover,
.input--search,
.popover-content,
.navDropDown-head,
.notification-item,
.navDropDown-footer,
.navDropDown-link,
.btn,
.conversationList-msg {
    border: none !important;
}

.input,
.inputBox,
.secretCode-codeBox,
.toolbar,
.voucher-code,
.voucher-teaser,
.btn--disabled,
.btn:disabled,
.card-close:disabled,
.comments-item,
.threadItem-metaRibbons,
.softMessages-item,
div.border.border--color-borderGrey.bRad--a.space--v-1.space--h-2 {
    border: none !important;
}

.text--color-price,
.vote-temp,
.input--search,
.vote-temp--hot,
.conversationList-msgPreview,
span.bg--color-white:nth-child(1) {
    color: #fff !important;
}

.vote-btn {
    background-color: #fff !important;
}

.subNavMenu-link--selected::after {
    background-color: #e3e3e3 !important;
}

.ico--type-comment-charcoal,
.cept-save-btn,
.thread-title--list::after {
    display: none !important;
}

.subNav,
.cept-breadcrumbsList {
    font-weight: 700 !important;
}

.overflow--fade::after,
.thread-title--card::after,
.overflow--fade-b-r--s::after,
.notifications-content {
    background: none !important;
}

.card {
    color: #cfcfcf !important;
    font-weight: normal !important;
}

.nav-link--selected {
    border-bottom: 3px solid #e3e3e3 !important;
}

.text--overlay::before {
    background-image: linear-gradient(90deg, hsla(0, 0%, 100%, 0), #2a2a2e 90%) !important;
}

.bg--inverted {
    background: transparent !important;
}

.link {
    color: #7cc2ef !important;
}

.tabbedInterface-tab--selected {
    color: #075ea4 !important;
}

.tabbedInterface-tab {
    color: white;
}


.vote-box,
.fadeOuterEdge--l {
    box-shadow: none !important;
}

.input-caretLeft,
.input-caretLeft::before {
    border-right: none !important;
}

.subNavMenu-item--separator,
.commentList-item {
    border-bottom: none !important;
}

.navDropDown-head {
    font-weight: bold !important;
}

.user {
    color: inherit !important;
}

.progressBar-progress {
    background-color: #b96161;
}

.userHtml a {
    color: #8cc5f2;
}
}

@-moz-document domain("www.hotukdeals.com") {
.border--color-brandPrimary {
    border: none !important;
}
}
