css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("http://greatescapefestival\\.com/gig-planner/(thursday|friday|saturday)/") {

  div.spinny {display: none;}
  div.grain {display: none;}
  div#ieCheck {display: none;}
  
  div#timeline p {background: #EEE; color: #000; font-weight: bold;}
  
  div#venues {max-height: 10000px; height: auto;}
  div#venues a {background: #EEE; color: #000; font-weight: bold; height: 102px;}
  div#events {overflow: visible; max-height: 10000px; height: auto;}
  div#events .row .eve {background: #DFD; color: #000; font-weight: bold;}
  
  div#schedule {width: 9259px;}
  div#schedule div#venues {width: 150px;}
  div#schedule div#timeline {margin-left: 150px;}
  
  .page-template-page-gig-planner.page .grid__item.main {max-width: 10000px;}
  div.spons {height: 15px !important;}
  div.sponsor {height: 15px !important;}
  div.evens {background: #FFF; border: solid #333 1px;}
  
  footer.site__footer {display: none;}
}
