css:
@-moz-document domain("kissmanga.com") {

	/* COLORS */

	/** DESCRIPTION : set the colors **/
	:root {
                --body-bg: #F9F8F8 !important;
		--card-bg: #F0F1F5 !important;
		--border-color: #BEBEBE !important;
		--link-color: #546A7B !important;
		--primary-color: #212121 !important;
		--secondary-color: #7E7E7E !important;
		--invert: invert(0) !important;
	}

	/* GENERAL */

	/** DESCRIPTION : set main font and background, plus remove some elements **/
	* {
		font-family: "Roboto", "ぼくたちのゴシック２ボールド" !important;
	}

	*::-moz-focus-inner {
		border: 0;
	}

	a {
		color: #3498DB;
	}

	#result_box a span,
	span[style="font-weight: bold; color: #d5f406"] {
		color: #3498DB !important;
		font-weight: normal !important;
	}

	html {
		background: var(--body-bg) !important;
	}

	body,
	body > #containerRoot {
		background: var(--body-bg) !important;
		overflow-x: hidden !important;
	}

	a,
	a:hover {
		transition: all 0.3s ease-in-out 0s !important;
	}

	a:hover {
		opacity: .7 !important;
	}

	select {
		-webkit-appearance: none;
		-moz-appearance: none;
		text-indent: 0px;
		text-overflow: ellipsis;
		white-space: nowrap;
	}

	#formVerify * {

		/*** VERIFY BOX ***/
		font-size: 14pt !important;
	}

	#adsIfrme7,
	div[style="float: left; width: 250px; text-align: left; margin-left: 2px"],
	div[style="text-align: center;"] > div[style="display: inline-table;"],
	.divCloseBut,
	#divFloatLeft,
	#divFloatRight,
	#divAds2,
	iframe,
	img[src="/Content/images/bookmark1.png"],
	#navsubbar a[href="/Register/Member"] {
		display: none !important;
	}

	#disqus_thread iframe {
		display: block !important;
	}

	#footer {

		/*** FOOTER ***/
		display: none !important;
	}
    
        :root {
		--avatar: url("https://vignette.wikia.nocookie.net/nichijou/images/e/e4/Anime-hakase-kawaii-nichijou-Favim.com-218145.jpg/revision/latest?cb=20121221073705") !important;
		--banner: url("http://stuffpoint.com/anime-and-manga/image/322583-anime-and-manga-one-piece-colorful.jpg") !important;
		--hot: url("https://s7.postimg.cc/52wxo944r/00_FJbu.png") !important;
		--link: url("http://icon-park.com/imagefiles/link_icon_blue.png") !important;
		--logo: url("https://s7.postimg.cc/x1r3fm5wp/v_RRZ6_X.png") !important;
		--next: url("https://s7.postimg.cc/stwb6i9if/Rk0_Cq3.png") !important;
		--prev: url("https://s7.postimg.cc/j9cojmrw7/tr_Eva_Q.png") !important;
		--search: url("https://www.freeiconspng.com/uploads/flat-search-find-icon-13.png") !important;
        }

	/* HEADER */
    
	/** DESCRIPTION : change the elements of the header/upper part **/
	#head h1 {
		background: no-repeat var(--banner) center !important;
		background-size: cover !important;
		border-top: 10px solid #2980b9 !important;
		border-bottom: 65px solid #2C3E50 !important;
		height: 200px !important;
		width: 100% !important;
		position: absolute !important;
		top: -30px !important;
		left: 0px !important;
	}

	#head h1 a.logo {
		background: var(--logo) no-repeat !important;
		background-size: auto 135px !important;
		width: 80% !important;
		padding-bottom: 10px !important;
		margin-top: 25px !important;
		margin-left: 20px !important;
		z-index: 9999 !important;
	}

	#navbar {

		/*** NAVBAR ***/
		background: transparent !important;
	}

	#navcontainer {
		display: none;
	}

	#navsubbar {

		/*** SUB-BAR ***/
		background: #2C3E50 !important;
		padding: 20px 0px !important;
		position: absolute !important;
		margin-top: 106px !important;
	}

	#navsubbar p {
		color: transparent !important;
	}

	#navsubbar p a {
		font-size: 15pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		line-height: 18pt !important;
		margin-top: -7px !important;
		max-width: 25% !important;
		overflow: hidden !important;
		text-overflow: ellipsis !important;
		white-space: nowrap !important;
	}

	#navsubbar p a:nth-of-type(1) {
		position: absolute !important;
		left: 16px !important;
	}

	#navsubbar p a:nth-of-type(2) {
		position: absolute !important;
		left: 165px !important;
	}

	#navsubbar p a:nth-of-type(3) {
		position: absolute !important;
		left: 325px !important;
	}

	#navsubbar p a:nth-of-type(4) {
		position: absolute !important;
		left: 450px !important;
	}

	#navsubbar p a:nth-of-type(5) {
		position: absolute !important;
		left: 555px !important;
	}

	#search {

		/*** SEARCHBAR ***/
		position: absolute !important;
		top: 216px !important;
		right: 30px !important;
		z-index: 999 !important;
	}

	#search input.text,
	#selectSearch {
		background: #404D63 !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		font-size: 15pt !important;
		font-stretch: condensed !important;
		line-height: 30px !important;
		padding-left: 10px;
	}

	#selectSearch {
		height: 35px;
		width: 150px !important;
		text-indent: 2px;
		margin-left: 5px;
	}

	#search img {
		display: none !important;
	}

	div[style="margin: 10px 48px 10px 0"] {
		width: 0px !important;
		padding: 0px !important;
		margin-top: -37px !important;
		margin-left: 380px !important;
	}

	div[style="margin: 10px 48px 10px 0"] a {
		background: no-repeat var(--search) center !important;
		background-size: 28px auto !important;
		color: transparent !important;
		font-size: 11pt !important;
		font-weight: bold !important;
	}

	#result_box {
		background-color: var(--body-bg) !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .3) !important;
		opacity: 1;
		padding: 10px 20px !important;
		width: 88.7% !important;
		margin-top: 16px !important;
		margin-left: -56px !important;
	}

	#result_box a {
		color: var(--primary-color);
		font-size: 14pt !important;
		font-stretch: condensed !important;
		padding: 10px 0px !important;
		position: static !important;
	}

	#result_box a:hover {
		background: transparent;
	}

	.dIa,
	.bp,
	#widget_bounds,
	#plusone,
	#___plusone_0,
	#search input.button {
		display: none !important;
	}

	#topHolderBox {

		/*** ACCOUNT ***/
		background-color: transparent !important;
		color: transparent !important;
		position: fixed !important;
		top: 40px !important;
		right: -45px !important;
		text-align: left !important;
		z-index: 9999 !important;
	}

	#topHolderBox span {
		color: transparent !important;
	}

	#topHolderBox img {
		display: none !important;
	}

	#aDropDown {
		background: no-repeat var(--avatar) center !important;
		background-color: #181818 !important;
		background-size: cover !important;
		border: none !important;
		border-radius: 50% !important;
		padding: 40px 0px 0px 40px !important;
		height: 0px !important;
		width: 0px !important;
	}

	#aDropDown small {
		display: none !important;
	}

	#menu_box {
		background: #2C3E50 !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		position: absolute !important;
		top: -25px !important;
		margin-left: -390px !important;
	}

	#menu_box a {
		border: none !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		padding: 0px 5px 5px 5px !important;
	}

	#menu_box a:first-child {
		padding-top: 10px !important;
	}

	#menu_box a:last-child {
		padding-bottom: 10px !important;
	}

	#menu_box a:hover {
		background: transparent !important;
		opacity: .7 !important;
	}

	/* HOME */

	/** DESCRIPTION : change/remove the elements in the front page (aka home) **/
	#leftside,
	#rightside {

		/*** LEFT/RIGHT SIDE ***/
		margin-top: 200px !important;
	}

	#leftside {

		/*** LEFT SIDE ***/
		margin-left: -20px !important;
	}

	#leftside .banner {

		/*** RANDOM BOX ***/
		background: var(--card-bg) !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		padding: 10px !important;
		min-height: 250px !important;
		max-height: 250px !important;
		margin-bottom: 15px !important;
	}

	#leftside .banner .bigChar {
		color: var(--primary-color) !important;
		font-size: 18pt !important;
		font-stretch: condensed;
		font-weight: normal !important;
		letter-spacing: 0px !important;
		text-shadow: none !important;
	}

	.info {
		color: var(--secondary-color) !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		text-transform: lowercase !important;
	}

	#leftside .dotUnder {
		background: #508AA8 !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		color: #FFF !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		line-height: 31pt !important;
		padding: 5px 10px 6px 10px !important;
		margin-left: 5px;
		margin-right: -7px;
	}

	#leftside #btnBookmarkManager,
	#leftside #spanBookmarkAdd,
	#leftside #spanBookmarkRemove {
		border-radius: 2px !important;
		color: #FFF !important;
		font-size: 14pt !important;
		font-stretch: condensed;
		padding: 7px 12px 8px 12px !important;
	}

	#leftside #btnBookmarkManager {
		background: #16A086 !important;
	}

	#leftside #spanBookmarkAdd {
		background: #16A086 !important;
	}

	#leftside #spanBookmarkRemove {
		background: #7E7E7E !important;
	}

	#leftside #spanBookmarkAdd #btnAddBookmark,
	#leftside #spanBookmarkRemove #btnRemoveBookmark {
		color: #FFF;
	}

	.banner p {
		color: var(--secondary-color);
		font-size: 14pt !important;
		font-stretch: condensed;
		line-height: 18pt !important;
	}

	.details a img,
	div[style="text-align: center"] img,
	.scrollable .items a img,
	#subcontent div a img,
	.tooltip img {
		border: 1px solid var(--border-color) !important;
		padding: 0px !important;
	}

	#leftside .bigBarContainer {

		/*** LATEST UPDATE BOX ***/
		background: var(--card-bg) !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		padding: 10px !important;
	}

	.barTitle {
		background: transparent !important;
		border-radius: 2px !important;
		padding: 9px 13px 7px 10px !important;
		margin-bottom: 8px !important;
	}

	.barTitle .scrollable_title {
		color: var(--primary-color);
		font-size: 18pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		letter-spacing: 0px !important;
		text-shadow: none !important;
	}

	a.prev {
		background: var(--prev) !important;
		background-size: 30px auto !important;
		background-position: center center !important;
		margin-top: 5px !important;
		filter: brightness(0) var(--invert) !important;
		opacity: .5;
	}

	a.next {
		background: var(--next) !important;
		background-size: 30px auto !important;
		background-position: center center !important;
		margin-top: 5px !important;
		margin-left: -8px !important;
		filter: brightness(0) var(--invert) !important;
		opacity: .5;
	}

	.navi {
		font-size: 16pt !important;
		font-weight: normal !important;
		text-transform: lowercase;
	}

	.arrow-general {
		display: none !important;
	}

	.barContent {
		background: transparent !important;
		color: var(--primary-color);
		font-size: 14pt !important;
		font-stretch: condensed !important;
		line-height: 20pt !important;
	}

	p[style="text-align: justify;"] {
		text-align: left !important;
	}

	.scrollable {
		height: 230px !important;
	}

	.scrollable .items a {
		color: #3498DB !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		line-height: 18pt !important;
	}

	.scrollable .items a img {
		margin-bottom: 5px !important;
	}

	#tabmenucontainer {

		/*** MANGALIST BOX ***/
		background: #444 !important;
		width: 100% !important;
	}

	#submenu {
		margin-top: -8px !important;
	}

	#tabmenucontainer li a {
		background: #174C4F !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
		padding: 10px 30px 10px 28px !important;
		text-align: left !important;
		text-transform: uppercase !important;
		white-space: nowrap !important;
	}

	#tabmenucontainer li a.tabactive {
		background: var(--card-bg) !important;
		color: var(--secondary-color);
	}

	#leftside #subcontent {
		background: var(--card-bg) !important;
		border: none !important;
		border-radius: 2px;
		border-top-left-radius: 1px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		padding: 10px !important;
	}

	#subcontent div div {
		background: var(--card-bg) !important;
	}

	#subcontent div div a,
	#subcontent div div a span {
		color: #3498DB;
	}

	#subcontent p a {
		font-size: 14pt !important;
		font-stretch: condensed !important;
	}

	#subcontent span.title {
		color: var(--primary-color) !important;
		font-size: 17pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
	}

	img[src*="hot.png"] {
		background: no-repeat var(--hot) left !important;
		background-size: cover !important;
		height: 0px !important;
		width: 0px !important;
		padding: 20px 0px 0px 20px !important;
		margin-bottom: 1px;
		margin-left: -7px;
	}

	img[src*="plus.png"],
	img[src*="minus.png"],
	img[src*="Content/images/numbers"] {
		display: none !important;
	}

	div[style="text-align: right; font-size: 16px"] {
		text-align: center !important;
	}

	div[style="text-align: right; font-size: 16px"] a[href="/MangaList/Newest"] {
		background: #60646D !important;
		border-radius: 2px !important;
		color: #FFF !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
		padding: 8px 18px !important;
		text-transform: uppercase !important;
	}

	div[style="text-align: right; font-size: 14px; padding-top: 5px"] a[href="/Status/Ongoing/LatestUpdate"],
	div[style="text-align: right; font-size: 14px; padding-top: 5px"] a[href="/MangaList/LatestUpdate"] {
		background: #557780 !important;
		border-radius: 2px !important;
		color: #FFF !important;
		font-stretch: condensed !important;
		font-style: normal !important;
		font-weight: bold !important;
		padding: 8px 18px !important;
		text-transform: uppercase !important;
		margin-right: -10px !important;
	}

	#rightside .rightBox {

		/*** RIGHT SIDE BOXES ***/
		background: var(--card-bg) !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		padding: 10px !important;
		margin-bottom: 12px !important;
	}

	#rightside .rightBox .barTitle {
		color: var(--primary-color);
		font-size: 18pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		letter-spacing: 0px !important;
	}

	div[style="margin-top: -5px; font-weight:bold;"] {
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		line-height: 18pt !important;
	}

	#rightside .rightBox:nth-child(7) {
		display: none;
	}

	#rightside .rightBox .barContent a,
	#rightside .rightBox .barContent a b {
		color: var(--link-color);
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-style: italic;
		font-weight: normal;
	}

	img[src*="bullet.png"] {
		background: no-repeat var(--link) center !important;
		background-size: cover !important;
		height: 0px !important;
		width: 0px !important;
		padding: 15px 0px 0px 15px !important;
		filter: brightness(0) var(--invert) !important;
		margin-top: 5px !important;
		margin-right: 3px !important;
	}

	/* SEARCH ENGINE */

	/** DESCRIPTION : change the elements in the search page **/
	.listing {
		margin-top: -40px !important;
	}

	#leftside .barTitle {
		color: var(--primary-color);
		font-size: 18pt;
		font-stretch: condensed !important;
		font-weight: normal !important;
		letter-spacing: 0px !important;
	}

	.listing .head {
		visibility: hidden !important;
	}

	.listing td {
		background: var(--card-bg) !important;
		color: var(--primary-color);
		z-index: 9999 !important;
	}

	.listing td a {
		color: var(--primary-color);
		font-style: italic;
		font-weight: normal !important;
	}

	.listing td a:hover,
	.episodeVisited {
		color: #3498DB !important;
	}

	.listing th {
		color: var(--primary-color);
		font-stretch: condensed !important;
		font-weight: normal;
	}

	.tooltip {
		background: var(--body-bg) !important;
		border-radius: 2px;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		padding: 30px 30px 80px 30px !important;
	}

	.tooltip .bigChar {
		font-size: 18pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		letter-spacing: 0px !important;
	}

	.tooltip p {
		color: var(--secondary-color);
		font-size: 14pt !important;
		font-stretch: condensed;
		line-height: 18pt !important;
		max-height: 100% !important;
		overflow: hidden !important;
		text-overflow: ellipsis !important;
	}

	.tooltip div {
		margin-left: 15px !important;
	}

	#formProfile span[style="font-size: 15px; font-weight: bold"],
	.barContent span[style="font-size: 15px; font-weight: bold"] {
		font-size: 15pt !important;
		font-weight: normal !important;
	}

	#status,
	#password,
	#username,
	#email,
	#retypePassword {
		background: var(--body-bg) !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		color: var(--primary-color) !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		padding: 5px 6px !important;
	}

	.lblAdvanceSearch {
		color: var(--primary-color) !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
	}

	#formAdvanceSearch #genres li {
		margin-bottom: 10px !important;
	}

	#formAdvanceSearch a {
		background: transparent !important;
		font-size: 14pt !important;
		font-stretch: condensed;
		white-space: nowrap;
	}

	#btnReset,
	#btnSubmit {
		margin-top: -20px !important;
	}

	/* MANGA LIST */

	/** DESCRIPTION : change the elements of the manga list page **/
	.alphabet {
		background: transparent !important;
		color: transparent !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		text-align: left !important;
		margin-top: -10px !important;
		margin-bottom: 0px !important;
	}

	.alphabet a {
		font-weight: bold;
		margin-right: -8px !important;
	}

	div.pagination ul.pager li {
		background: transparent !important;
		border: none !important;
		color: var(--primary-color) !important;
		font-weight: bold !important;
	}

	div.pagination ul.pager li,
	div.pagination ul.pager li a {
		color: var(--primary-color) !important;
		font-size: 14pt !important;
		font-stretch: condensed;
		font-weight: normal;
	}

	div.pagination ul.pager li a:hover {
		border: none !important;
		background: transparent !important;
		margin-top: 0px !important;
		margin-left: 0px !important;
	}

	/* MANGA DETAIL */

	/** DESCRIPTION : change the elements of the manga details page **/
	#leftside .bigChar {
		color: var(--primary-color) !important;
		font-size: 17pt !important;
		font-stretch: condensed;
		letter-spacing: 0px !important;
	}

	.barContent.chapterList {
		margin-top: 22px !important;
	}

	#leftside .bigBarContainer .listing {
		color: #FFF !important;
		margin-top: -125px !important;
	}

	#leftside div[style="text-align: center"] {
		position: absolute;
		margin-top: -138px !important;
		margin-left: 100px !important;
	}

	#leftside div[style="text-align: center"] img {
