css:
@-moz-document url-prefix("https://www.twitch.tv/") {
/* Chat border and scrollbars */
 .scrollable-area.tw-flex-grow-1.chat-list__lines > .simplebar-scroll-content > .simplebar-content {
    background: #090909 !important;
}
.scrollable-area.tw-flex-grow-1.chat-list__lines > .horizontal.simplebar-track > .simplebar-scrollbar {
    visibility: hidden !important;
}
.scrollable-area.tw-flex-grow-1.chat-list__lines > .vertical.simplebar-track {
    visibility: hidden !important;
}
.tw-pd-x-1.tw-pd-b-1.tw-block.chat-input {
    background: #090909 !important;
}
.tw-pd-b-1.tw-flex-grow-1.tw-full-height {
    /* background: #090909 !important; /* Use only if not using split chat colors */
    margin-left: -9px !important;
    margin-right: -9px !important;
}
/* Sub notice color */
 .ffz--subscribe-line.tw-pd-r-2.tw-pd-y-05.user-notice-line {
    background: #111 !important;
}
.ffz--subscribe-line.tw-pd-r-2.tw-pd-y-05.user-notice-line > .tw-c-text-alt-2 {
    color: #999 !important;
}
/* split chat color */
 .chat-line__message:nth-of-type(2n+2) {
    background: #101010
}
.tw-textarea--no-resize.tw-textarea {
    border: none !important;
}
.tw-pd-b-1.tw-full-height.tw-flex-grow-1 {
    color: #c1c1c1
}
.tw-textarea--no-resize.tw-textarea.tw-full-width.tw-font-size-6.tw-border-radius-medium.tw-block {
    background: #101010
}
.tw-pd-r-1.tw-pd-l-1.tw-justify-content-center.tw-full-width.tw-flex-shrink-0.tw-flex.tw-c-background-base.tw-border-b.tw-align-items-center.stream-chat-header {
    height: 1px !important;
    overflow: hidden;
}
.tw-pd-r-1.tw-pd-l-1.tw-justify-content-center.tw-full-width.tw-flex-shrink-0.tw-flex.tw-c-background-base.tw-border-b.tw-align-items-center.stream-chat-header:hover {
    height: 40px !important;
    visibility: visible;
}
.tw-right-0.tw-mg-r-1.tw-absolute > .tw-tooltip-wrapper.tw-relative.tw-inline-flex {
    height: 1px !important;
    overflow: hidden;
}
.tw-right-0.tw-mg-r-1.tw-absolute > .tw-tooltip-wrapper.tw-relative.tw-inline-flex {
    height: 46px !important;
    visibility: visible;
}
.tw-right-0.tw-left-0.tw-flex-nowrap.tw-flex-column.tw-flex.tw-bottom-0.tw-absolute.chat-viewers__pane > .tw-full-height.tw-flex-nowrap.tw-flex-column.tw-flex {
    background-color:#090909;
}
}
