css:
@-moz-document url-prefix("http://www.douban.com"), url-prefix("http://www.douban.com"), url-prefix("https://www.douban.com"), url-prefix("https://www.douban.com")
{
  
/*主页右侧栏底部*/
[id^="dale_homepage_login_inner_middle"]  {
    display: none !important;
  }

[id^="dale_homepage_login_top_right"]  {
    display: none !important;
  }

[id^="movie_home_left_bottom_frame"]  {
    display: none !important;
  }

/*主页右侧栏广告*/

[id^="dale_homepage_login_bottom_right"]  {
    display: none !important;
  }



 /*豆瓣正在发生*/
  .aside .notify-mod  {
    display: none !important;	
  }

  /*豆瓣时间*/
  [id^="anony-time"] {
    display: none !important;
  }

 .customize-slot   {
    display: none !important;
  }

 /*热门话题*/
  .rec_topics  {
    display: none !important;	
  }

 .mod:nth-of-type(4) > h2 {
    display: none !important;	
  }

.mod:nth-of-type(5) > h2 {
    display: none !important;	
  }

 .aside .mod {
    margin-bottom: 0px;
}

 /*创建小站*/
 [id^="fp-sites"] {
    display: none !important;
  }

 /*话题广场右上角NEW*/
img[src="https://img3.doubanio.com/f/shire/e49eca1517424a941871a2667a8957fd6c72d632/pics/new_menu.gif"]  {
      display: none !important;
}

/*我的小组话题广告*/
.pro-banner {
      display: none !important;
}

/*下载豆瓣客户端*/
.top-nav-doubanapp {
      display: none !important;
}

}
