css:
@-moz-document url-prefix("https://forum.android.com.pl"), url-prefix("http://forum.android.com.pl") {

/*[[theme-color]]*/

button.owl-next:before,
html[dir="ltr"] #rss_feed .ipsCarousel_shadowRight{
    background-image: linear-gradient(to right, transparent, var(--secondary-bg-color));
}
button.owl-prev:before,
html[dir="ltr"] #rss_feed .ipsCarousel_shadowLeft{
    background-image: linear-gradient(to left, transparent, var(--secondary-bg-color));
}

.material-circle-btn,
.ipsCarousel .ipsCarousel_nav{
    background: var(--main-bg-color);
    color: #fff;
}

h3.ipsWidget_title {
    width: 100%;
    border-radius: 0 !important;
}

body,
.cPastLeaders_cell,
.cPastLeaders_cell div,
.card,
.card.reddit .reddit-title{
    background: var(--main-bg-color) !important;
    color: var(--main-text-color) !important;
}
a {
    color: var(--link-color);
}

#ipsLayout_header nav.ipsNavBar_noSubBars, .ipsLayout_minimal #ipsLayout_header nav,
#ipsLayout_header nav.ipsNavBar_noSubBars:after{
    background: transparent !important;
}

#ipsLayout_header header,
#elMobileNav,
.ipsBox,
.ipsAreaBackground_reset,
.ipsPad_half,
.ipsApp ul.ipsPad_half,
.ipsApp ol.ipsPad_half,
.ipsBox:not( .ipsBox_transparent):not( .ipsModerated),
.ipsMenu_title,
.ipsPagination_active,
.ipsHovercard,
.ipsAreaBackground,
.ipsPad,
.ipsApp .ipsButton_primary,
.ipsDialog_title,
.ipsDialog_title ~ .ipsHr,
.ipsDialog > div,
.ipsTabs_panel,
.ipsTabs,
.ipsMenu,
.ipsMenu_innerContent,
.cAuthorPane_mobile,
.ipsSelectTree_nodes [data-role="nodeList"],
#elTabsReport,
.ipsTabs_panels.ipsTabs_contained,
#elFullNotifications_menu .ipsDataItem,
.ipsTable_zebra tbody tr:nth-child( even ):not(.ipsTable_highlight):not(.ipsModerated){
    background: var(--secondary-bg-color) !important;
    border: none;
}

.ipsPagination .ipsPagination_page.ipsPagination_active a{
    background: var(--active-page-color) !important;
}

#ipsLayout_header nav,
.ipsNavBar_secondary,
.ipsNavBar_primary:not( .ipsNavBar_noSubBars) > ul:before,
.ipsNavBar_secondary > li > a,
.ipsNavBar_primary > ul > li.ipsNavBar_active > a,
.ipsNavBar_secondary > li.ipsNavBar_active a,
ul:not( .ipsMenu_keyNav) .ipsMenu_item:not( .ipsMenu_itemClicked):not( .ipsMenu_itemDisabled) a:not( .ipsMenu_itemInline):hover,
.ipsMenu_item[data-selected] a,
.ipsMenu_item[data-selected] span,
.ipsMenu_item.ipsMenu_hover,
.ipsPagination .ipsPagination_page a,
.ipsPagination .ipsPagination_next a,
.ipsPagination .ipsPagination_prev a,
.ipsPagination .ipsPagination_first a,
.ipsPagination .ipsPagination_last a,
.ipsApp .ipsButton_important,
.ipsFollow,
.ipsButtonBar .ipsButtonRow li > a,
.ipsButtonBar .ipsButtonRow li > button,
.ipsButtonBar .ipsButtonRow li > span,
.ipsQuote_citation,
.ipsQuote .ipsQuote_citation a,
.ipsSpoiler_header,
.ipsMenu_headerBar,
.ipsMenu_footerBar,
.ipsReact_reactCount,
.ipsReact_reactCount span,
.ipsApp .ipsField_autocomplete, .ipsApp textarea, .ipsApp input[type="text"], .ipsApp input[type="password"], .ipsApp input[type="datetime"], .ipsApp input[type="datetime-local"], .ipsApp input[type="date"], .ipsApp input[type="month"], .ipsApp input[type="time"], .ipsApp input[type="week"], .ipsApp input[type="number"], .ipsApp input[type="email"], .ipsApp input[type="url"], .ipsApp input[type="search"]:not(#elSearchField), .ipsApp input[type="tel"], .ipsApp .ipsField_dummy,
.ipsSelectTree_nodes [data-role="nodeList"] li .ipsSelectTree_item:not( .ipsSelectTree_itemDisabled ):hover,
.ipsSelectTree,
.cForumMiniList.cForumMiniList_multiRoot > li > a{
    background: var(--third-bg-color) !important;
    color: var(--main-text-color) !important;
}
.ipsDataList_readStatus .ipsDataItem:not( .ipsDataItem_unread):not( .ipsDataItem_selected):not( .ipsModerated),
.ipsDialog_loading,
#elSearchExpanded .ipsSideMenu_list,
.ipsQuote,
.ipsSpoiler,
#elInput_messenger_title,
.ipsField_autocomplete,
#elInput_messenger_to_dummyInput,
#elInput_topic_title,
#elFullNotifications_menu .ipsDataItem_unread,
html[dir] .ipsButton_split > .ipsButton.ipsButton_link, html[dir] .ipsButton_split > li > .ipsButton.ipsButton_link{
    background: var(--main-bg-color) !important;
    border: none;
}
.ipsMenu,
.ipsHovercard {
    box-shadow: 0px 0px 30px -10px rgba(0, 0, 0, 1);
    border-radius: 10px;
}
#elFlashMessage{
    background: var(--main-bg-color) !important;
    box-shadow: 0px 0px 20px 0px rgba(0,0,0,0.75);
}

#elFullNotifications_menu .ipsDataItem{
    border-bottom: 1px solid var(--main-bg-color);
}

.ipsNavBar_secondary > li > a:hover {
    color: var(--link-hover-color);
}
.ipsBreadcrumb,
.ipsBreadcrumb a,
.ipsType_pageTitle,
.ipsWidget_title,
.ipsType_sectionHead,
.ipsType_light,
.ipsBreadcrumb > ul > li,
.ipsType_richText,
.ipsDataItem_main a,
.ipsDataItem_lastPoster li a,
.ipsDataItem_withPhoto li a,
.ipsWidget .ipsTabs_small .ipsTabs_item:not( .ipsTabs_activeItem),
.ipsMenu a,
.ipsLoading:before,
.ipsPagination_pageJump a,
.ipsPager_type,
html[dir="ltr"] .ipsPager .ipsPager_prev a:after,
html[dir="rtl"] .ipsPager .ipsPager_next a:after,
.ipsDialog_title,
#ipsLayout_footer a,
#ipsLayout_footer p,
.fa.fa-search,
::placeholder,
.ipsButton_light,
.ipsComposeArea_dummy.ipsJS_show,
.ipsType_veryLarge,
.ipsType_minorHeading,
input[type="text"],
.ipsPager .ipsPager_next a:after,
.ipsPager .ipsPager_prev a:after,
.ipsRichEmbed_itemTitle,
.ipsSideMenu_title,
.ipsTable.ipsMatrix .ipsMatrix_subHeader,
a.ipsType_sectionTitle, .ipsType_sectionTitle a{
    color: var(--main-text-color) !important;
}
.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected):not( .ipsModerated):not( .ipsDataItem_new):not( .ipsDataItem_success):not( .ipsDataItem_warning):not( .ipsDataItem_error):not( .ipsDataItem_info):not( .ipsDataItem_status):nth-child(even),
.ipsTable th,
.ipsSelectTree_nodes,
.ipsFollow .ipsApp .ipsButton_link,
.ipsTable.ipsMatrix .ipsMatrix_subHeader,
.ipsTable.ipsMatrix .ipsMatrix_highlighted{
    background: var(--fourth-bg-color);
}
.ipsDataItem_subList .ipsDataItem_unread a,
.ipsDataItem_unread .ipsDataItem_title a,
.ipsDataItem_unread .ipsDataItem_title {
    color: var(--link-color) !important;
}
.ipsComment_highlighted {
    background: var(--secondary-bg-color)!important;
    box-shadow: 0px 2px 10px rgba(186,43,43,0.4) !important;
    border: none !important;
}

.ipsComment_selected{
    animation: ipsSelectedShadow 2s ease infinite;
    box-shadow: 0 1px 1px rgba(0,0,0,0.075), 0px 1px 5px rgba(225, 231, 7, .6);
}

.ipsComment_selected .ipsComment_author,
.ipsComment_selected .ipsColumn,
.ipsComment_selected .ipsColumn .ipsPad{
    animation: ipsSelectedBg 2s ease infinite;
}

@keyframes ipsSelectedShadow{
    0%{
        box-shadow: 0 1px 1px rgba(0,0,0,0.075), 0px 1px 5px rgba(3, 234, 191, .6);
    }
    50%{
        box-shadow: 0 1px 1px rgba(0,0,0,0.075), 0px 1px 5px rgba(225, 231, 7, .6);
    }
    100%{
        box-shadow: 0 1px 1px rgba(0,0,0,0.075), 0px 1px 5px rgba(3, 234, 191, .6);
    }
}
@keyframes ipsSelectedBg{
    0%{
        background: #33779f;
    }
    50%{
        background: #2f557d;
    }
    100%{
        background: #33779f;
    }
}



.ipsMenu.ipsMenu_bottomCenter:after {
    border-color: transparent transparent var(--secondary-bg-color) transparent;
}
li #elTopicActions,
li #elTopicActionsBottom{
    background: #B71C1C !important;
    color: #fff;
}
#elModCPContent .ipsDataItem_new{
    background: #b71c1c4d;
}
#elModCPContent .ipsDataItem_warning {
    background: #fb8f0073;
}
.ipsDialog_close,
.ipsType_noLinkStyling,
.ipsType_noLinkStyling:hover{
    color: #feffa1;
}
.ipsStreamItem_unread .ipsItemStatus i{
    color: #FDD835;
}
.cForumRow[data-categoryid="839"] .ipsType_sectionTitle{
    background: #FF6F00 !important;
}
.ipsBox .ipsModerated,
.cPost.ipsModerated .cAuthorPane,
.ipsModerated .ipsPad,
.ipsModerated .ipsColumn,
.ipsModerated .ipsType_sectionHead {
    background: #070707 !important;
}
.cke_wysiwyg_frame,
.cke_wysiwyg_div,
.ipsSideMenu_item:not( .ipsSideMenu_itemActive) a:hover,
a.ipsSideMenu_item:not( .ipsSideMenu_itemActive):hover,
span.ipsSideMenu_item:not( .ipsSideMenu_itemActive):hover,
.ipsStreamItem_contentType,
.ipsComment_header,
.ipsPhotoPanel_mini,
.ipsComment:not( .ipsModerated) .ipsComment_header,
input[type="text"],
.ipsAttachment_dropZone.ipsDragging,
.ipsComposeArea_dropZone.ipsDragging,
.ipsAutocompleteMenu_item,
.ipsAlert,
iframe[data-embedContent].ipsEmbed_finishedLoading{
    background: var(--secondary-bg-color) !important;
}
.ipsReact_button {
    filter: none !important;
}
.cke_top,
.ipsAreaBackground_light,
#elSearchExpanded,
#elSearch,
.ipsApp .ipsDataItem_selected,
.ipsButton_light,
.ipsComposeArea_dummy.ipsJS_show,
.ipsTabs_activeItem,
.ipsWidget.ipsWidget_vertical .ipsWidget_title,
.ipsWidget.ipsWidget_horizontal .ipsWidget_title,
.ipsAutocompleteMenu_item[data-selected],
#elInput_comment_edit_reason,
.ipsType_sectionTitle,
.cForumMiniList > li > a.cForumMiniList_selected{
    background: var(--third-bg-color) !important;
}
#elSearch input[type="search"],
#elSearchExpanded,
#elSearchExpanded .ipsMenu_title,
.ipsTabs_activeItem,
.ipsStreamItem.ipsStreamItem_contentBlock,
.ipsFollow  .ipsApp .ipsButton_link{
    border: none !important;
    color: var(--main-text-color);
}
html[dir="ltr"] .ipsStream_withTimeline .ipsStreamItem:after,
html[dir="ltr"] .ipsStream_withTimeline .ipsStreamItem.ipsStreamItem_contentBlock:before{
    border-color: transparent var(--secondary-bg-color) transparent transparent;
}

a.cke_button{
    filter: invert(1);
}

button[type="submit"].ipsButton_primary,
button[data-action="ok"].ipsButton.ipsButton_fullWidth.ipsButton_primary{
    background: #4CAF50 !important;
}

/*przyciski moderacyjne w cke*/
.cke_button[title*="warn"],
.cke_button[title*="WARN"],
.cke_button[title*="Notka"]
{
    filter: none !important;
}

/*Wybór czcionki i jej rozmiaru*/
.cke_panel_grouptitle {
    background: var(--secondary-bg-color);
    color: #fff;
    text-shadow: none;
    box-shadow: none;
}
.cke_panel_listItem a {
    background: var(--third-bg-color);
    border: 1px solid var(--fourth-bg-color);
}
.cke_panel_listItem a:hover,
.cke_panel_listItem a:focus,
.cke_panel_listItem a:active,
.cke_combo_off a.cke_combo_button:hover{
    background: var(--fourth-bg-color);
}
a.cke_combo_button,
.cke_combo_on a.cke_combo_button {
    background: var(--third-bg-color);
    box-shadow: none;
    border: none;
}

.cke_combo_text {
    color: #fff;
    text-shadow: none;
}
.cke_combo_on a.cke_combo_button {
    background: var(--secondary-bg-color);
}

#elFullReports_menu .ipsDataItem.ipsDataItem_unread a,
.fa-flag {
    color: red !important;
}
#elFullReports_menu .fa-exclamation-triangle,
#elReportSidebar .fa-exclamation-triangle{
    color: #FFA726;    
}
#elFullReports_menu .fa-check-circle,
#elReportSidebar .fa-check-circle{
    color: #4CAF50;    
}

.ipsMessage_error,
.ipsAreaBackground_negative{
    background: #cc5353 !important;
}
.ipsMessage_warning{
    background: #d87c4f !important;
}
.ipsMessage_information {
    background: #5e95bc !important;
}
.ipsMessage_general {
    background: #7f858d !important;
}
.ipsMessage_success {
    background: #479c87 !important;
}

#grid-toggle-btn a{
    border: 0;
}

.ipsDataItem{
    border-color: var(--third-bg-color);
}
.nbPinned,
#ipsLayout_footer{
    background: var(--third-bg-color) !important;
}
#ipsLayout_footer{
    border-top: 0;
}
.cIPInfo_map,
.cPastLeaders_title span,
.ipsStreamItem.ipsStreamItem_actionBlock,
.cProfileHeader_name{
    background: transparent !important
}

.ipsColumn_medium img {
    max-width: 100%;
}

.ipsHovercard_stemTop .ipsHovercard_stem,
.ipsHovercard_stemTop .ipsHovercard_stem:before,
.ipsMenu.ipsMenu_topCenter:after,
.ipsMenu.ipsMenu_topCenter:before{
    border-color: var(--secondary-bg-color) transparent transparent transparent;
}
.ipsHovercard_stemBottom .ipsHovercard_stem,
.ipsHovercard_stemBottom .ipsHovercard_stem:before,
.ipsMenu.ipsMenu_bottomCenter:before{
    border-color: transparent transparent var(--secondary-bg-color) transparent;
}

.ipsGrid.ipsGrid_collapsePhone .ipsDataItem.cForumGrid,
.ipsGrid.ipsGrid_collapsePhone .ipsDataItem.cForumGrid .ipsPhotoPanel,
.ipsGrid.ipsGrid_collapsePhone .ipsDataItem.cForumGrid .ipsPhotoPanel_mini,
.ipsGrid.ipsGrid_collapsePhone .ipsDataItem.cForumGrid .ipsPad{
    background: var(--fourth-bg-color) !important;
}

/*animacja ładowania embed*/
#ipsEmbedLoading{
    background: var(--main-bg-color) !important;
}
@keyframes dummy_anim {
    0% {
        background-color: var(--main-bg-color);
    }
    80% {
        background-color: var(--secondary-bg-color);
    }
    100% {
        background-color: var(--third-bg-color);
    }
}

.ipsApp select:not( [multiple] ) {
    background: var(--main-bg-color);
    color: var(--main-text-color);
    border: 0;
    background-image: url("https://forum.android.com.pl/uploads/set_resources_13/84c1e40ea0e759e3f1505eb1788ddf3c_select_dropdown.png");
    background-position: right center;
    background-size: 18px 9px, 100% 100%;
    background-repeat: no-repeat, repeat;
    
}

.ipsCode{
    background: var(--fourth-bg-color) !important;
    border-left-color: var(--third-bg-color) !important;
}


.ipsCode span{
    filter: invert(100%);
}

/*member*/
[style*="color: #111111;"] {
    color: #9E9E9E !important;
}

/*Super Member*/
[style*="color: #333425"] {
    color: #90a4ae !important;
}

/*Weteran*/
[style*="color: #3f3f3f;"] {
    color: #78909c !important;
}

/*często używane kolory tekstu na forum zlewające się z tłem*/
[style*="color:#000"],
[style*="color:#252525"],
[style*="background-color:#ffffff"],
[style*="color: rgb(51, 51, 51)"],
[style*="color:#272a34"],
[style*="color:#333333"],
[style*="color:#2b2b2c"],
[color*="#2b2b2c"],
[style*="background-color:#fffefa"],
[style*="color:#000000"],
[style*="color: #000000"],
font[color*="000"],
[style*="background-color:rgb(230,230,230)"],
[style*="color:#222222;"],
[style*="color: rgb(82, 82, 82)"],
[style*="rgb(10,10,10)"],
[style*="background-color:#fcfcfc"],
[style*="rgb(51,51,51)"],
[style*="rgb(39, 42, 52)"],
[style*="background-color:#f9f9f9"],
[style*="color:rgb(82,82,82)"],
[style*="color:#263238"],
[style*="rgb(237, 237, 237)"],
span[style*="rgb(255, 255, 255)"],
[style*="background-color:rgb(255,255,255)"],
[color="#333333"],
[style*="color:#353c41"],
[style*="color:#212121"],
[style*="background-color:#ebfeff"],
[style*="rgb(250, 250, 250)"],
[style*="color:black"],
[style*="color:#2a2e2e"],
[style*="color: #2b2e2f"],
[style*="color:#2b2e2f"],
[style*="color:#535353"],
[style*="color=#2f353d"],
[color="#2f353d"],
[style*="rgb(39,42,52)"],
[color*="#262626"],
[style*="color:#544d44"],
[bgcolor*="#f"],
[style*="color:rgb(59,59,59)"],
[color*="#0"],
[style*="rgb"] {
    color: var(--main-text-color) !important;
    background: transparent !important;
}

::-webkit-scrollbar-track {
    background-color: var(--secondary-bg-color);
}
::-webkit-scrollbar {
    width: 10px;
}
::-webkit-scrollbar-thumb {
    background-color: var(--third-bg-color);
}
::selection {
    background: #90A4AE;
    color: #fff;
}
}
