css:
@-moz-document domain("topachat.com") {
/*Header*/

	/*Header Color*/
		#masthead {background: #34393F !important;}
	/*Primary-nav Color, Menu*/
		#primary-nav {background: #303133;}

/*Background*/
html {background: #262626 !important;} 

/*Hyperlink Color*/
a {color: #006a99;}



}

@-moz-document domain("topachat.com") {
/*Produits + X-BLANKED-X */
.product-sheet {
    background: #34393f !important;
} 
.cart-box {
    background-color: #232528 !important;
    background-repeat: no-repeat;
}

.button.cart, input[type="submit"].cart {
    background: #65AD42 url(https://www.topachat.com/css/images/basket-picto-2016.png) 95% top no-repeat;
    color: #fff;
    border: 1px solid #243e18;
    text-shadow: 0 2px 0 #243e18;
    box-shadow: 0 1px 0 #34393f,0 0 1px #34393f inset,0 1px 0 1px #34393f,0 1px 0 0px rgba(52,57,63,0.0);
    transition: all 100ms ease-in-out;
}

.button.cart:hover, input[type="submit"].cart:hover, .button.cart:focus, input[type="submit"].cart:focus {
    top: 2px;
    box-shadow: 0 1px 0 #232528,0 0 2px #232528 inset,0 3px 0 1px #232528,0 1px 0 2px rgba(35,37,40,0.1);
}

.detail2 #content {

    background: #34393f;

}

ul.tabs-content.contained > li {

    background: #232528;
    border: 1px solid #bbb;

}

#descriptif-produit {
    background: #34393f;

}

#primary-nav a {

    color: #FFF;

}
.wysiwyg-color-blue {

    color: #0055ff;

}

.ariane li.current {
	
    display: inline-block;
    font-weight: bold !important;
    letter-spacing: 0.05em !important;
    padding: 0 .5em 0 15px;
    text-indent: .5em;
    color: #dedede;
    background: #787877;
    position: relative;
    padding-left: 15px;

}

.ariane {

    clear: both;
    background: #f00;
        background-image: none;
    background: #787877;
    font-size: 0.75em !important;
    height: 30px;

}

ul.tabs-content.contained > li > section {background:#34393f}
.caracLine:nth-of-type(2n) {background:#23272b}

/*Page des Composants*/
#boxCatGamme {background:#242424;}
#boxMainProduct1 {background:#242424;}
.titleCat {background:#161616;border:1px solid #2a2a2a;box-shadow: inset 0 0 1px 1px #161616, 0 0 1px 1px #161616;}
div.boxMainProductTop1{background:#161616;border:1px solid #2a2a2a;box-shadow: inset 0 0 1px 1px #161616, 0 0 1px 1px #161616;}
.boxlabelTop1 a {color:#666666;}

/*Liste des Composants*/
.products-list .mode-affichage, .products-list .filtres-texte, .filtres { background:#202020;}
label.combo{background:#161616}
select {background:#161616}
.filter-box .ruler{background:#161616}
.filter-box .ruler .data {background:#161616}
.js .filter-box [data-type-filter="combo"].open div, .js .filter-box [data-type-filter="combo"] {background:#323232}
.tacenter {background: #262626 !important;}
}

@-moz-document domain("topachat.com") {
/*Configurateur PC*/

.tacenter {
    background: #262626 !important;
}

.last-config li:nth-of-type(2n+1) {background:#333333}
.last-config li:hover {background: #161616;}
#guides-assistants li a {background:#101010}
#config-maker {background:#202020}
.select-compo {background:#161616}
.list-compos.open {background:#333333}
.list-compos .best-seller {background:#202020}
.list-compos td {background:#161616}
#config-preview {background:#161616}
#config-preview .total{background:#404040}
}

@-moz-document domain("topachat.com") {
/*Homepage*/

#produits-home a.produit {color: #006A99;background: #303133;}
#produits-home .top-list a.produit-big .preview {background: #303133;}
#produits-home .top-list a.produit-big .libelle {background: #303133;}
#produits-home .top-list a.produit-big {color: #006A99;}
#produits-home .top-one .libelle {background: #303133;}
#produits-home .top-one .more {background: #303133;}
#produits-home .top-list a.produit, #produits-home .top-list a.produit-big {border: 1.5px solid #000;}
.box-twitter .tweet {background: #303133;}
.box-twitter .tweet-user {background: #242424;color: #555;}
#categories .listProduct ul {background:#262626;}
#categories .listProduct .GAMME {background: #303133;}
#categories .listProduct .GAMME .CATEGORY {background: #161616}
#guides-assistants, #tutos-youtube {background:#161616;}
#guides-assistants .title, #tutos-youtube .title {background:#262626}
#top-marques {background:#161616;}
#top-marques .title {background:#262626;}
#top-marques a {background:#070707;}
}

@-moz-document domain("topachat.com") {
/*Font Shit*/

#primary-nav a {
    color: #006A99;
    margin-right: 0;
}

html, button, input, select, textarea {
    color: #ababab;
}

/* source-sans-pro-regular - latin */
@font-face {
  font-family: 'Source Sans Pro';
  font-style: normal;
  font-weight: 400;
  src: local('Source Sans Pro Regular'), local('SourceSansPro-Regular'),
       url('http://essicolor.ca/assets/fonts/source-sans-pro/sourcesanspro-regular-webfont.woff2') format('woff2'), /* Chrome 26+, Opera 23+, Firefox 39+ */
       url('http://essicolor.ca/assets/fonts/source-sans-pro/sourcesanspro-regular-webfont.woff') format('woff'); /* Chrome 6+, Firefox 3.6+, IE 9+, Safari 5.1+ */
}

/* source-sans-pro-italic - latin */
@font-face {
  font-family: 'Source Sans Pro';
  font-style: italic;
  font-weight: 400;
  src: local('Source Sans Pro Italic'), local('SourceSansPro-Italic'),
       url('http://essicolor.ca/assets/fonts/source-sans-pro/sourcesanspro-it-webfont.woff2') format('woff2'), /* Chrome 26+, Opera 23+, Firefox 39+ */
       url('http://essicolor.ca/assets/fonts/source-sans-pro/sourcesanspro-it-webfont.woff') format('woff'); /* Chrome 6+, Firefox 3.6+, IE 9+, Safari 5.1+ */
}

/* source-sans-pro-700 - latin */
@font-face {
  font-family: 'Source Sans Pro';
  font-style: normal;
  font-weight: 700;
  src: local('Source Sans Pro Bold'), local('SourceSansPro-Bold'),
       url('http://essicolor.ca/assets/fonts/source-sans-pro/sourcesanspro-bold-webfont.woff2') format('woff2'), /* Chrome 26+, Opera 23+, Firefox 39+ */
       url('http://essicolor.ca/assets/fonts/source-sans-pro/sourcesanspro-bold-webfont.woff') format('woff'); /* Chrome 6+, Firefox 3.6+, IE 9+, Safari 5.1+ */
}
/* source-sans-pro-700italic - latin */
@font-face {
  font-family: 'Source Sans Pro';
  font-style: italic;
  font-weight: 700;
  src: local('Source Sans Pro Bold Italic'), local('SourceSansPro-BoldItalic'),
       url('http://essicolor.ca/assets/fonts/source-sans-pro/sourcesanspro-boldit-webfont.woff2') format('woff2'), /* Chrome 26+, Opera 23+, Firefox 39+ */
       url('http://essicolor.ca/assets/fonts/source-sans-pro/sourcesanspro-boldit-webfont.woff') format('woff'); /* Chrome 6+, Firefox 3.6+, IE 9+, Safari 5.1+ */
}

@-moz-document domain("topachat.com")
{

  /* Set main text colour, size and spacing 
  body
  {
    -webkit-font-smoothing: antialiased;
    text-rendering: geometricPrecision;
    word-spacing: normal !important;
    letter-spacing: normal !important;
    color: #222 !important;
    font-size: 20px;
    line-height: 1.5em !important;
  }
    
  Set the font everywhere */
  body, 
  font, 
  input, 
  td, 
  th, 
  a
  {
    font-family: 'Source Sans Pro', sans-serif !important;
  }

  /* Make links a nicer colour, no !important so sites can style their own links
  a
  {
    color: #05f;
    text-decoration: none;
  }
   
  a:visited
  {
    color: #c60 !important;
  }

  a:hover
  {
    text-decoration: underline;
  }
  
  /* Add some spacing to the text 
  td, 
  th, 
  input[type=text],
  input[type=url]
  {
    padding: 4px 8px !important;
  } */

  /* Shrink additional info text size!
  #additional-info
  {
    font-size: 16px;
  } */
} 

}
