css:
@-moz-document url-prefix("https://www.mega-debrid.eu/") {
#section_navigation,
#copyright,
#statsFooter,
p:empty,
.acp-box .bpf p {
	display: none;
}
body,
#branding,
#page_body,
#navigation,
table,
#main_content .acp-box p {
	background: #36454f!important;
}
body {
	font: normal normal 15px Tahoma, Verdana, Arial, sans-serif;
	color: #fff;
}
#header {
	border-bottom: 0px;
}
#main_content {
	margin-left: 0px;
	border-top: 1px solid #36454f;
	padding-bottom: 5px;
}
#main_content .acp-box h3 {
	background: #16A085;
	text-align: center;
}
#main_content .acp-box p:first-child {
	margin-top: 20px;
}
#main_content .acp-box p:first-child a {
	background: #16A085;
	font-weight: 700;
	font-size: 1.1em;
	color: #fff;
	padding: 7px;
}
#main_content .acp-box p a {
	color: #fff;
}
}

