@-moz-document url-prefix("https://www.mega-debrid.eu/") {
textarea {
	display: block;
	max-width: 95%;
	min-width: 95%;
	padding: .625em;
	resize: vertical;
	background-color: #fff;
	border-color: #dbdbdb;
	color: #363636;
	box-shadow: inset 0 1px 2px rgba(10, 10, 10, .1);
	font-size: 1.1rem;
	align-items: center;
	border: 1px solid transparent;
	border-radius: 25px;
	height: 2.25em;
	justify-content: flex-start;
	line-height: 1.5;
	padding-bottom: calc(.375em - 1px);
	padding-left: calc(.625em - 1px);
	padding-right: calc(.625em - 1px);
	padding-top: calc(.375em - 1px);
	position: relative;
}
.button,
.realbutton {
	color: white;
	border-radius: 20px;
	text-shadow: 0 1px 1px rgba(0, 0, 0, 0.2);
	background: #16A085;
	font-size: 2.25em;
}
}

@-moz-document url-prefix("https://www.mega-debrid.eu/") {
#main_content .tab_bar {
	background: #fff!important;
}
#main_content .tab_bar.no_title {
	padding: 7px 15px 29px;
	width: 94%;
}
#tab_links a {
	color: white;
}
#main_content .tab_bar li.active,
#main_content .tab_bar li.tabon {
	background: #16A085;
}
}

@-moz-document url-prefix("https://www.mega-debrid.eu/index.php?page=offres") {
li {
	color: black;
}
.button,
.realbutton {
	color: white;
	border-radius: 20px;
	text-shadow: 0 1px 1px rgba(0, 0, 0, 0.2);
	background: #16A085;
	font-size: 14px;
}
input {
	margin-top: 10px;
}
tr {
	background: #16A085;
}
.table > thead > tr > td.active,
.table > tbody > tr > td.active {
	background-color: #36453f;
}
.table > thead > tr > td.active,
.table > tbody > tr > td.active:hover {
	color: black
}
}

