}

.ts-videos article{
    border-radius: 6px;
    margin-bottom: 30px;
    background-color: #fff;
    border: none;
    box-shadow: 0 4px 32px 0px rgba(0,0,0,0.15);
    overflow:hidden;
}

.wide-screen .home-videos-left img{
    border-bottom-left-radius:0;
    border-bottom-right-radius:0;
}

.ts-videos article img{
    border-radius:4px;
}

.home-videos-right article{
    box-shadow:none;
    border-bottom: 1px solid #f6f6f6;
    margin:0;
    border-radius:0;
    padding:15px 10px !important;
}

.birsey_yaz{
    border-radius: 6px;
    margin-bottom: 30px;
    background-color: #fff;
    padding: 15px;
    border: none;
    box-shadow: 0 4px 32px 0px rgba(0,0,0,0.15);
}

.liste{
    border-radius: 6px;
    margin-bottom: 30px;
    background-color: #fff;
    padding: 15px;
    border: none;
    box-shadow: 0 4px 32px 0px rgba(0,0,0,0.15);
}

.col-left article{
    border-radius: 6px;
    margin-bottom: 30px;
    background-color: #fff;
    padding: 15px;
    border: none;
    box-shadow: 0 4px 32px 0px rgba(0,0,0,0.15);
}

.navbar-default .navbar-nav>li>a {
    color: #000;
    font-weight: 600;
    padding: 15px 13px;
}

.navbar-default .navbar-nav>li>a:hover{
    color:#4285f4;
}

body{
    background:#fff;
    font-family: 'Open Sans', sans-serif;
}

.h1, h1 {
    line-height: 1.4;
}

.widget_one_cikanlar article {
border: 0;
    box-shadow: none;
    min-height: 85px;
    padding:10px 0;
    margin: 10px 0;
    border-bottom: 1px solid #f6f6f6;
}

.widget .widget_title{
    margin-bottom:0;
}

.widget .widget_title:after{
    content:'';
    display:block;
    height:3px;
    background:#c00;
    width:40px;
    margin-top:10px;
    margin-bottom:15px;
}

.home-videos .tabs>li.active{
    border-bottom: 3px solid #4285f4;
}

.btn-primary {
    color: #fff;
    background-color: #4285f4;
    border-color: #2b6fe0;
}

#yeni-icerik{
    background: #4285f4;
    border: none;
    padding: 12px 0;
    color: #fff;
    box-shadow: 0 0 0 0 rgba(66, 133, 244, 1);
    animation: pulse 2s infinite;
    width: 100px;
    height: 100px;
    border-radius: 50%;
    padding-top: 42px;
    margin: 0 auto;
    margin-bottom: 30px;
    transition: all .3s;
}

#yeni-icerik:Hover{
    background:#1257ca;
    color:#fff;
    transform:scale(1.1);
}



@-webkit-keyframes pulse {
  0% {
    -webkit-box-shadow: 0 0 0 0 rgba(66, 133, 244, .6);
  }
  70% {
      -webkit-box-shadow: 0 0 0 10px rgba(66, 133, 244, 0);
  }
  100% {
      -webkit-box-shadow: 0 0 0 0 rgba(66, 133, 244, 0);
  }
}
@keyframes pulse {
  0% {
    -moz-box-shadow: 0 0 0 0 rgba(66, 133, 244, .6);
    box-shadow: 0 0 0 0 rgba(66, 133, 244, .6);
  }
  70% {
      -moz-box-shadow: 0 0 0 10px rgba(66, 133, 244, 0);
      box-shadow: 0 0 0 10px rgba(66, 133, 244, 0);
  }
  100% {
      -moz-box-shadow: 0 0 0 0 rgba(66, 133, 244, 0);
      box-shadow: 0 0 0 0 rgba(66, 133, 244, 0);
  }
}



.wide-screen .home-videos-left h1 {
    font-size: 20px;
    margin-top: 7px;
}

.widget_teknoseyir_gundemi a{
    margin-bottom: 7px;
        color: #4285f4;
}

a{
    transition: all .3s;
}

.col-right .content a:hover, .ts-videos h1 a:hover{
    color: #4285f4;
}

.col-right .content a, .ts-videos h1 a {
    color: rgba(0, 0, 0, 0.68);
}
.col-right .content .fa{
    opacity:0.4;
}

a{
     transition:all .3s;
}

.post-actions{
    border-top:1px solid #f6f6f6;
    padding-top:15px;
    margin-top:15px;
}

.post-actions a{
margin-right:12px;
 font-weight:600;
}

.post-actions a:hover{
    color:#4285f4;
}

.puan{
    margin: 6px 0;
}

.puan i{
    color: #d9534f;
    opacity:.9 !important;
}

.widget_one_cikanlar .wp-post-image{
    margin-right:8px;
}
.widget_one_cikanlar img{
    border-radius:4px !important;
}

.widget img{
    border-radius:50% !important;
}
.widget_nostalji img{
    border-radius:4px !important;
}
