css:
@-moz-document domain("tumblr.com") {
#dashboard_index,



#dashboard_inbox,



#dashboard_edit_post,



#dashboard_submissions,



#dashboard_drafts,



#dashboard_post_queue,



#dashboard_likes,



#dashboard_followers,



#dashboard_following,



#dashboard_tagged,



#dashboard_directory,



#dashboard_lookup_contacts,



#main_doc_page,



#dashboard_settings,



#dashboard_create_tumblelog,



#dashboard_members,



#main_tag,



#main_meetups,



#main_about,



#main_explore,



#dashboard_goodies,



#main_tagged,



#main_developers,



#main_themes,



#dashboard_manage_themes,



#main_edit_theme,



#main_jobs,



#main_policy,



#dashboard_tumblelog_settings {



background:#ca546a url(https://i.imgur.com/G05Quwm.jpg) top left fixed! important }



#dashboard_index div.l-content,



#dashboard_inbox div.l-content,



#dashboard_submissions div.l-content,



#dashboard_drafts div.l-content,



#dashboard_post_queue div.l-content,



#dashboard_likes div.l-content,



#dashboard_tagged div.l-content,



#dashboard_followers div.l-content,



#dashboard_members div.l-content,



#main_tag div.l-content,



#dashboard_tumblelog_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



background:rgba(0,0,0,0) !important }



.selection_nipple { border-bottom-color: rgba(0,0,0,0) !important }



.permalink {background: rgba(0,0,0,0) !important }



.avatar_frame {



background-color: rgba(0,0,0,0) !important; border-color: rgba(0,0,0,0) !important }



.notification {



background: rgba(0,0,0,0) !important;



border: rgba(0,0,0,0) !important;



background-image: none !important;



margin-bottom: 1px !important }



.notification a { border-bottom: 1px solid rgba(0,0,0,0) !important }



.notification a:hover { border-bottom: 1px solid rgba(0,0,0,0) !important }



.notification_type_icon { display: none !important }



.nipple { border-right-color: rgba(0,0,0,0) !important }



.controls_section li { background: rgba(0,0,0,0) !important }



.with_subtitle small { color: rgba(0,0,0,0) !important }



.recessed a { color: rgba(0,0,0,0) !important }



.radar { display: none !important }



* { text-shadow: none !important }



h1, .title { color: rgba(0,0,0,0.6) !important }



.no_posts_found, h1.dashboard_header { color: rgba(0,0,0,0) !important }



.dashboard_options_form {



background: rgba(0,0,0,0) !important;



border: none !important;



color: rgba(0,0,0,0) !important }



.dashboard_options_form_disclaimer, .dashboard_options_form_disclaimer a { color: rgba(0,0,0,0) !important }



.blue_note, .blue_note a {



background: rgba(0,0,0,0) !important;



color: rgba(0,0,0,0) !important }



#footer, #footer a {



color: rgba(0,0,0,0) !important }



img[src*="https://i.imgur.com/G05Quwm.jpg"] {



opacity: 0;



visibility: hidden }



.new_post {



background: #fff url(https://i.imgur.com/G05Quwm.jpg) center bottom }



.new_post_label {



-webkit-text-stroke:0 !important;



color: rgba(0,0,0,0) !important;



opacity:1 !important;



top: 0px !important;



padding-top: 80px }



.new_post_label:hover { color: rgba(0,0,0,0) !important }



#pagination a {



background: rgba(0,0,0,0) !important;



color: rgba(0,0,0,0) !important }



#pagination a:hover {



color: rgba(0,0,0,0) !important }



#dashboard_controls a, #dashboard_controls .control {



color: rgba(0,0,0,0) !important }



.dashboard_controls_arrow_first_page, .dashboard_controls_arrow_left, .dashboard_controls_arrow_right {



display: none !important }



#right_column a#dashboard_switch_blog_menu_current:hover {



background: url(https://i.imgur.com/G05Quwm.jpg);}



#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {



  background: url(https://i.imgur.com/G05Quwm.jpg);}
}
