css:
/* i really want this to be global */

body,
#left {
    background-color: #151515 !important;
    color: #5AF !important;
}
#first div#layout a.layout_btn,
#first div#start a.start_btn {
    background: none;
}
#first div#layout a.layout_btn:hover,
#first div#start a.start_btn:hover {
    border-color: #aaf;
}
#screenshot {
    display: none;
}
#wrap {
    background: none;
    background-color: #222;
    color: #5AF;
    width: 1400px;
}
#left {
    margin: 0;
    padding-bottom: 5px;
}
.addthis_toolbox,
#follow {
    display: none;
}
#footer {
    display: none;
}
#meter {
    padding: 5px;
    color: #a50 !important;
    font-size: larger;
    border-color: #f6f;
    margin-left: 135px;
    background-color: #faf;
    -webkit-filter: invert(1);
    filter: invert(1);
}
#middle,
#text,
#type,
#typing_info,
#suggestion {
    width: 1000px;
    padding: 10px 2px;
}
#type,
#text {
    background-color: #000510;
    color: #36f;
}
#text > .current,
#text > .current_txt {
    color: #48f;
    border-bottom: 2px solid #48f;
}
#text > .done_ok,
#text > .done_txt_ok {
    color: #4e8;
}
#text > .done_bad,
#text > .done_txt_bad {
    color: #f44;
    border-bottom: 2px solid #f44;
}
#text > .next,
#text > .next_txt {
    color: #799;
}
#suggestion {
    margin-left: 150px;
}
.navigation {
    width: 100%;
}
#typing_info a {
    background-color: #051020;
}
div.kb div.key {
    background-color: #051020;
    color: #8bf;
}
div.kb div.key[style="background-color: rgb(255, 255, 255);"] {
    background-color: #051020 !important;
    color: #8bf;
}
div.kb div.key[style="background-color: rgb(0, 255, 0);"] {
    background-color: #060 !important;
    color: #0ff;
    border-color: #0f0;
    font-size: larger;
}
div.kb div.key[style="background-color: rgb(255, 0, 0);"] {
    background-color: #600 !important;
    color: #f00;
    border-color: #f00;
    font-size: larger;
}
select {
    background-color: #222;
    color: #7DF;
}
.graph {
    border: 1px solid #13263a !important;
}
.graph li {
    background-color: #151515 !important;
    color: #7DF !important;
    border-bottom: 1px solid #13263a !important;
}
.graph li:last-child {
    border-bottom: 0px solid #000 !important;
}
.graph .result {
    padding: 5px;
    color: #a50 !important;
    border-color: #d5d !important;
    background-color: #fae !important;
    -webkit-filter: invert(1);
    filter: invert(1);
}
input,
input.btn {
    background-color: #000;
    color: #5af;
    border: 1px solid #b0bdcc;
}
