css:
/* i really want this to be global */

#wrapper {width: 830px;}

#help a{color:#5f7360;}

/* Background -DONE */
#content {background: rgba(0, 0, 0, 0.0);;}
body,body.gui,.popup {
color: #fff;
background: url("https://steamcdn-a.akamaihd.net/steamcommunity/public/images/items/258030/f23dd8ee045cf05e1509e39ad147dccba18b84c9.jpg") no-repeat fixed;
background-size: contain 100%;}

/* Text/Number fields */
.submitFooter .button,#menuMainList li a, #menuSubList li a,textarea,body input{
font-size:12px;
color: #fff;
padding: 5px 15px 5px 15px;
box-shadow: inset 0px 0px 15px rgba(0, 0, 0, .75);
border-radius: 7.5px;
background: linear-gradient(to bottom, rgba(125, 125, 125, 0.65) 0%,rgba(20, 20, 20, 0.65) 100%);
border: 0px solid #ffec08;}

select{
font-size:12px;
color: #fff;
padding: 5px 15px 5px 15px;
box-shadow: inset 0px 0px 15px rgba(0, 0, 0, .75);
border-radius: 7.5px;
background: linear-gradient(to bottom, rgba(125, 125, 125, 0.65) 0%,rgba(20, 20, 20, 0.65) 100%) !Important;
background-color: rgb(20, 20, 20) !Important;
border: 0px solid #ffec08;}

input.button{
color: #fff !Important;
padding: 5px 15px 5px 15px !Important;
box-shadow: inset 0px 0px 15px rgba(0, 0, 0, .75) !Important;
border-radius: 7.5px !Important;
background: linear-gradient(to bottom, rgba(125, 125, 125, 0.65) 0%,rgba(20, 20, 20, 0.65) 100%) !Important;
border: 0px solid #ffec08 !Important;}

#menuMainList li span{
 ;
font-size:12px;
color: #fff;
padding: 5px 15px 5px 15px;
box-shadow: inset 0px 0px 15px rgba(0, 0, 0, .75);
border-radius: 7.5px;
background: linear-gradient(to bottom, rgba(255,246,0,0.75) 0%,rgba(254,245,0,0.75) 1%,rgba(153,147,0,0.75) 100%);
border: 1px solid #ffec08;}

body input[name="save_button"]:hover,body input[name="apply_button"]:hover,body input[name="reset_button"]:hover,#menuMainList li a:hover,#menuSubList li a:hover{
 ;
font-size:12px;
color: #fff;
padding: 5px 15px 5px 15px;
box-shadow: inset 0px 0px 15px rgba(0, 0, 0, .75);
border-radius: 7.5px;
background: linear-gradient(to bottom, rgba(125, 125, 125, 0.65) 0%,rgba(20, 20, 20, 0.65) 100%);
border: 1px solid #ffec08;}

/* Titles */
h4,div.bulle,#header h2,h2,body fieldset legend {
background-color: #d7d6b7;
padding: 5px 10px 5px 10px;
border-radius: 7.5px;
color: #5f7360;
/*box-shadow: inset 0px 0px 10px rgba(0, 0, 0, 0.35);*/
border: solid 3px #c3bf9e;
font-family: 'Roboto', sans-serif;
font-size:14px;
font-weight:900;
text-align: center;}

#help h2{
background-color: #d7d6b7;
padding: 5px 10px 5px 10px;
border-radius: 7.5px;
color: #5f7360;
/*box-shadow: inset 0px 0px 10px rgba(0, 0, 0, 0.35);*/
border: solid 3px #c3bf9e;
font-family: 'Roboto', sans-serif;
font-size:14px;
font-weight:900;
text-align: center;}


/* Backbone i.e. back of all windows/prompts */
table tr th, .submitFooter, #help, body fieldset {
 ;
font-size:12px;
border: 0px solid #fff;
color: #fff;
box-shadow: inset 0px 0px 20px rgba(255, 255, 255, 0.38);
border-radius: 7.5px;
background: linear-gradient(to bottom, rgba(255, 255, 255, 0.65) 0%,rgba(101, 101, 101, 0.65) 100%);}

body #content{background-color: rgba(0, 0, 0, 0);}

body #content.infopage #main {
background-color: rgba(0, 0, 0, 0.00);
/*box-shadow: inset 0px 0px 60px rgba(0, 0, 0, .75);*/
border: 0px solid #fff;}


/* Radio checker */

input.spaceradio {
    margin-right: 10px;
    box-shadow: inset 0px 0px 60px rgba(0, 0, 0, 0);
}
/* Meater */

.meter {
    background-color: rgba(0, 0, 0, 0.35);
    border: 0px solid #000;
    color: #fff;
    box-shadow: inset 0px 0px 60px rgba(0, 0, 0, .75);
    border-radius: 15px;
}
.meter .bar {
    background: linear-gradient(to bottom, rgba(252, 234, 187, 1) 0%, rgba(252, 205, 77, 1) 50%, rgba(248, 181, 0, 1) 51%, rgba(251, 223, 147, 1) 100%);
}
.meter .text {
    color: #f00;
    font-weight: bolder;
}
/* Logo */

#header .logo,
#header #logo {
    background: url(http://rekored.github.io/Assets/DD-WRT.svg) left center no-repeat;
}
#content::after {
     ;
    color: #fff;
    top: 10px;
    padding: 15px;
    content: "D-WRT © embeDD GmbH | Theme: Earthlock: Gnart Tigermoth v1 © 2018 RekoRed_";
}
div.also,
div.note,
.demo-container,
#content,
body #content {
    border: 0px solid #111111;
}
#header .navig a,
#t-graph li.day,
#t-graph,
.setting .default {
    color: #fff;
}
#help {
    padding-left: 10px;
    padding-right: 10px;
}

div.also,
div.note,
#menuSub,
#header {
    background-color: rgba(0, 0, 0, 0);
}
/* Buttons */

/* Unessacaryy big reboot router button */

body input.button {
    background-color: rgba(0, 0, 0, 0.35);
    border: 1px solid rgba(255, 255, 255, .20);
    color: #fff;
    box-shadow: inset 0px 0px 60px rgba(0, 0, 0, .75);
    border-radius: 15px;
}
/* CSS FOR WI-VIZ */

span.title3d {
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0.35) 0%, rgba(0, 0, 0, 0.75) 75%, rgba(0, 0, 0, 0) 100%);
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 7.5px;
    padding-bottom: 10px;
    border-radius: 15px;
    color: #fff;
    /*box-shadow: inset 5px 5px 30px rgba(255, 235, 59, 0.75);*/
}
div.slidingheader {
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0.35) 0%, rgba(0, 0, 0, 0.75) 75%, rgba(0, 0, 0, 0) 100%);
    padding-top: 7.5px;
    padding-bottom: 10px;
    border-radius: 15px;
    color: #fff;
    /*box-shadow: inset 5px 5px 30px rgba(255, 235, 59, 0.75);*/
}
div.slidingbodyshow {
    background-color: rgba(0, 0, 0, 0.35);
    border: 0px solid #000;
    color: #fff;
    box-shadow: inset 0px 0px 60px rgba(0, 0, 0, .75);
    border-radius: 15px;
}
