@-moz-document url-prefix("https://hangouts.google.com/call/") {
/* hide active video container */

div.qMwJZe {
    display: none !important;
}
/* bottom wrapper is not in bottom any more*/

div.G1OBde {
    top: 0 !important;
    left: 0 !important;
    height: 100% !important;
    width: 100% !important;
}
/* bottom controls wrapper */

div.L0HJ1e {
    z-index: 100;
    transform: none !important;
    top: 0;
}
/* bottom controls buttons */
/* disabled state .jY9Dbb */
/* enabled stat .U8OAre */

div.L0HJ1e div.O0WRkf.zZhnYe.e3Duub.C0oVfc {
    position: absolute;
    left: 0;
}
/* audio button */

div.L0HJ1e div.xdiH8e {
    top: -10px;
}
/* hang button */

div.L0HJ1e div.nfdz6d {
    top: 50px;
}
/* video button */

div.L0HJ1e div.WGQfVc {
    top: 110px;
}
/* top controls 
div.GhN39b 
*/
/* participants wrapper */

div.gSTPzb {
    top: 0 !important;
    left: 0 !important;
    height: 100% !important;
}
/* participant */

div.ZEZETe {
    position: absolute;
}
/* 
participant's outer video wrapper 
*/

div.ZEZETe div.Wmf6nf,
/* participant's inner video */

div.ZEZETe div.p2hjYe.zDyG0c {
    left: 0 !important;
    top: 0 !important;
    width: 100% !important;
    height: 100% !important;
}
/* participant's name
div.ZEZETe div.tLaQOe
*/
/* participant's video */

div.ZEZETe div.p2hjYe.zDyG0c video.Gv1mTb-aTv5jf {
    object-fit: cover;
}
/* participant's own video
div.ZEZETe div.p2hjYe.zDyG0c video.Gv1mTb-aTv5jf.Gv1mTb-PVLJEc
*/
/* participant's avatar */

div.ZEZETe div.ac5LHf img.cbflpe {
    object-fit: none;
}
}

style_setting_options.value:
@-moz-document url-prefix("https://hangouts.google.com/call/") {
/* 1x3 layout */

div.ZEZETe {
    position: absolute;
    width: 33%;
    height: 100%;
    border: solid 2px black;
    top: 0;
}
div.ZEZETe:nth-child(1) {
    left: 0;
}
div.ZEZETe:nth-child(2) {
    left: 33%;
}
div.ZEZETe:nth-child(3) {
    left: 66%;
}
}

@-moz-document url-prefix("https://hangouts.google.com/call/") {
/* 2x2 layout */

div.ZEZETe {
    position: absolute;
    width: 50%;
    height: 50%;
    border: solid 2px black;
}
div.ZEZETe:nth-child(1),
div.ZEZETe:nth-child(2) {
    top: 0;
}
div.ZEZETe:nth-child(3),
div.ZEZETe:nth-child(4) {
    top: 50%;
}
div.ZEZETe:nth-child(1),
div.ZEZETe:nth-child(3) {
    left: 0;
}
div.ZEZETe:nth-child(2),
div.ZEZETe:nth-child(4) {
    left: 50%;
}
}
