css:
@-moz-document domain(my.zerotier.com) {

/* Background */
#centralUiMain, #centralUiMain > div, div.container-fluid.row, html, html > body, div.row {
    background-color: #323232 !important;
}

#sign-in, input {
    color: #EEEEEE !important;
    background: #262626 !important;
}

/* Main UI */
h1, h2, h3, h4, h5, label, p, textarea, input, span.text-black, .heading a, .collapse-content li a {
    color: #EEEEEE !important;
}

.form-group, .small, a.zt-expandable-panel-section, a.list-group-item > div {
    color: #EEEEEE !important;
}

.panel, code.uri {
    color: #EEEEEE !important;
    background-color: #323232 !important;
    border: 1px #646464 solid !important;
}

.heading a, .heading, .inner pre, span.badge, .col-xs-12 > div > div.row {
    background-color: #262626 !important;
    border: none !important;
}

.panel-heading, textarea, input, .group-heading, .action-heading, .inner pre, span.badge {
    background-color: #262626 !important;
    border: 1px #646464 solid !important;
}

a.list-group-item, .panel-body, .well, td input, section.resource-group, .collapse-content li a {
    color: #EEEEEE !important;
    background-color: #323232 !important;
    border: none;
}

a.list-group-item, a.btn, strong code, dd code {
    background-color: #262626 !important;
    color: #EEEEEE !important;
    border: 1px #646464 solid !important;
}

.col-xs-12 > div[style="background-color: rgb(216, 216, 216); padding: 15px;"], .zt-network-rules-main, .zt-network-collapsable-help, td {
    background-color: #262626 !important;
    color: #EEEEEE !important;
    border: 1px #646464 solid !important;
}

.zt-user-subscription, .zt-api-access-token-list {
    background-color: #262626 !important;
    border: 1px #646464 solid !important;
}

/* Specials */
div.form-group.zt-network-id {
    background-color: #262648 !important;
}

a.back-to-top {
    background-color: #4848FF !important;
    color: #EEEEEE;
}

.CodeMirror {
    background-color: #262648 !important;
    color: #EEEEEE !important;
}

.CodeMirror-gutters {
    background-color: #181836 !important;
    color: #EEEEEE !important;
}

.zt-member-ip-button {
    border-bottom: none !important;
    color: #EEEEEE !important;
    fill: #EEEEEE !important;
}
}
