css:
@-moz-document domain("discordia.me") {
/* These are the basics for the main page UI, but they affect most things throughout the wiki */

.nav.is-blue-grey {
    background-color: #7289DA;
}
a {
    color: #7289DA;
}
.nav.is-blue-grey .nav-item .button {
    background-color: #7289DA;
}
.nav.is-blue-grey .nav-item .button.is-outlined {
    background-color: #7289DA;
}
.nav.is-blue-grey .nav-item .button:hover {
    background-color: #2C2F33;
}
.nav.is-blue-grey .control input[type=text] {
    background-color: #4960B1;
}
.is-alternate-blue-grey .sidebar aside .sidebar-label {
    background-color: #303337;
}
.is-alternate-blue-grey .sidebar {
    background-color: #2C2F33;
}
.sidebar {
    max-width: 350px;
}
.is-alternate-blue-grey .sidebar aside .sidebar-menu li>ul {
    background-color: #1F2226;
}
.hero {
    background-color: #23272A;
    border-bottom: 0px;
}
.is-primary-blue-grey .hero h1 {
    color: hsla(0, 0%, 100%, .7);
}
.mkcontent strong {
    color: hsla(0, 0%, 100%, .7);
}
.mkcontent h2 {
    background-color: #36393D;
    background: #36393D;
    border: 1px solid #7289DA;
    color: hsla(0, 0%, 100%, .7);
}
.mkcontent .indent-h2 {
    border-right: 5px solid #7289DA;
}
.hero h2 {
    color: hsla(0, 0%, 100%, .7);
}
.mkcontent a.toc-anchor:visited {
    color: hsla(0, 0%, 100%, .7) !important;
}
.mkcontent a.toc-anchor {
    color: hsla(0, 0%, 100%, .7);
}
.mkcontent h1 {
    background-color: #323539;
    background: linear-gradient(to bottom, #323539, #2C2F33);
    border-bottom: 0px;
    color: hsla(0, 0%, 100%, .7);
}
.mkcontent {
    background-color: #23272A;
    color: hsla(0, 0%, 100%, .7);
}
.mkcontent blockquote {
    background-color: #36393D;
    background: #36393D;
    border: 1px solid #7289DA;
    border-left-width: 7px;
    box-shadow: inset 0px 0px 0px 0px #fff;
}
.mkcontent blockquote p {
    color: hsla(0, 0%, 100%, .7);
}
.mkcontent h3 {
    background-color: #36393D;
    background: #36393D;
    border: 1px solid #7289DA;
    border-right-width: 5px;
    color: hsla(0, 0%, 100%, .7);
}
.mkcontent .indent-h3 {
    border-right: 5px solid #7289DA;
}
.footer.is-blurple {
    background-color: #1f2226;
}
.mkcontent code {
    color: hsla(0,0%,100%,.7);
    background-color: #2f3136;
    padding: .2em;
    border-radius: 3px;
}
/* NAVIGATION/ALL PAGES PAGE */

.has-collapsable-nav {
    background-color: #23272A;
}
.editor-toolbar {
    background: #23272A;
}
.collapsable-nav {
    background-color: #1f2226;
}
/* BACKEND CSS */

.column {
    background-color: #23272A;
}
.form-sections section {
    color: hsla(0, 0%, 100%, .7);
}
table {
    color: hsla(0, 0%, 100%, .7);
}
.table tbody tr:nth-child(odd) {
    background-color: #1f2226;
}
.mkcontent table tr:nth-child(even) {
    background-color: #1f2226;
}
.table tbody tr {
    background-color: #23272A;
}
.mkcontent table th:first-child {
    border-left-color: #9e9e9e;
}
.mkcontent table th:last-child {
    border-right-color: #9e9e9e;
}
.mkcontent table th {
    background-color: #738bd7;
    border: 1px solid #9e9e9e;
}
.toggle-text {
    color: hsla(0, 0%, 100%, .7);
}
.form-sections section .desc {
    color: hsla(0, 0%, 100%, .7);
}
.form-sections section .section-block h6 {
    color: hsla(0, 0%, 100%, .7);
}
/* MODAL CSS */

.modal-content header.is-light-blue {
    background-color: #7289DA;
}
.modal-content header.is-indigo {
    background-color: #7289DA;
}
.modal-content header.is-orange {
    background-color: #7289DA;
}
.button.is-light-blue:hover {
    background-color: #546BBC;
}
.button.is-indigo:hover {
    background-color: #546BBC;
}
.button.is-indigo {
    background-color: #7289DA;
    border-color: #657CCD;
}
.button.is-light-blue {
    background-color: #7289DA;
    border-color: #657CCD;
}
.button.is-orange {
    background-color: #7289DA;
    border-color: #657CCD;
}
.button.is-orange:hover {
    background-color: #546BBC;
}
}
