css:
@-moz-document domain("inoreader.com") {
.article_author {display: none !important; }
.article_tile_footer_feed_title {display: none;}
.article_feed_title {display: none;}
/*.ar {border-bottom: none; padding: 12px 7px 12px 12px;}*/
.ar {
    border-bottom: 1px solid #c1cad830;
    padding: 11px 11px 11px 12px;
}
#reader_pane.reader_pane_view_style_0 .article_header_title {
font-size: 1.2em;
}
#reader_pane {
    background-color: #45998833;
}
#reader_pane.reader_pane_view_style_5 #reader_pane {
    background-color: #25975626 !important;
}

#reader_pane.reader_pane_view_style_5 .article_tile.article_unreaded.article_expanded, .article_magazine.article_subscribed {
    background-color: #ffffffff !important;
}

#subscriptions_buttons, #sb_rp_tools, #sb_tp_search_overlay {background-color: #5986c3; background-image: none; } 
#feed_searcher {background-color: azure; box-shadow:none; }
#sb_rp_heading, .inno_toolbar_button {text-shadow: none;} 
#sb_rp_subscribe, #sb_rp_clip_page, #sb_rp_create_as, #sb_rp_add_team {background-color: #2726263b; text-shadow:none}
#feed_searcher {color: black !important;}

.article_title_expanded_view {top: 12px;}
.article_magazine_feed_title {color: #bdc1c7;}
.article_unreaded .article_magazine_feed_title {color: #bdc1c7;}
.icon-radio_checked_big {color: #d5d7d9;}
.article_magazine_footer {top: 6px;position: relative;margin-bottom: 29px;}

.tree_unreaded, .unread_cnt {color: #05a !important;} 
.tree_unreaded {font-weight: normal;} 

.icon-tag_full:before {color: #8e9195;}

#tree .feed_favicon {width: 13px; height: 13px;}

.article_magazine_footer .article_magazine_feed_title {display: none;} 
div.article_magazine { max-width: 900px;} 
div.article_card.article_current {border: 4px solid rgba(49, 97, 196, 0.6);border-radius: 12px;}
div.article_expanded {border: 4px solid rgba(49, 97, 196, 0); border-radius: 12px;}

.article_stripe {width: 99.3%; z-index: -1; background-color: #ffffff;}

/*div.article_expanded {background-color: #2e415c00;}
.article_full_contents {background-color: #2e415c00 !important;}
.reader_pane_view_style_1 .article_footer, .reader_pane_view_style_1 .arrow_div {background-color: #d4d40a00 !important;}*/

#preferences_wrapper .tr2 {background-color: #bbcfe7;}

.article_header_title {font-weight: normal !important;}
.article_title_link {font-weight: normal;}
.article_card .article_title, #article_dialog .article_title {padding-bottom: 20px; padding-top: 22px;}
.article_card .article_sub_title {position: absolute;}
}
