css:
@-moz-document url("https://www.rolegate.com/") {
.App {
	font-family: "Open Sans", sans-serif;
	height: 100vh;
}
.App pre {
	font-family: "open sans-serif", sans-serif;
}
a:hover {
	color: #ffffff;
}
.home_screen .app_logo {
	margin: 0;
	height: 20vh;
}
.home_screen .menu {
	min-height: 30vh;
	margin: 10vh 0;
}
.full_screen {
	background: url("https://www.rolegate.com/");
	background-size: cover;
	background-color: #3a3a3a;
	height: 100vh;
}
.home_screen .container {
	margin-bottom: 2vh;
}
.game_screen .story_area .dialogue .content_area .content,
.game_screen .story_area .chat .content_area .content,
.game_screen .chat_area .dialogue .content_area .content,
.game_screen .chat_area .chat .content_area .content {
	border: solid 2px transparent;
	border-radius: 5px;
	padding: 10px;
	position: relative;
	background-color: #2a2a2a;
	color: #ffffff;
}
.home_screen .button_large:hover:not(:disabled),
.home_screen .button_flat:hover:not(:disabled),
.home_screen .button_square:hover:not(:disabled) {
	background-color: rgba(205, 205, 205, 0.6);
}
.game_screen .story_area,
.game_screen .chat_area {
	background-color: #1a1a1a;
}
.game_menu.container {
	font-size: 2vh;
	text-align: center;
	min-height: 5vh;
	max-height: 5vh;
}
.game_menu.container,
.write_box.container {
	background-color: #0a0a0a;
}
.game_menu .button_square {
	background-color: #0a0a0a;
	min-height: 4.5vh;
	max-height: 4.5vh;
}
.chat_area .line.chat .author_area .name {
	font-size: 1vh;
}
.button_square .button_title {
	text-align: center;
	font-size: 1.25vh;
	/* margin-top: 5px; */
}
.button_large,
.button_flat,
.button_square {
	background-color: rgba(65, 65, 65, 0.3);
}
.button_large:hover:not(:disabled),
.button_flat:hover:not(:disabled),
.button_square:hover:not(:disabled) {
	background-color: rgba(49, 29, 29, 0.6);
}
.button_large:active:not(:disabled),
.button_flat:active:not(:disabled),
.button_square:active:not(:disabled) {
	background-color: rgba(55, 55, 55, 0.7);
}
.button_large:hover:not(:disabled),
.button_flat:hover:not(:disabled),
.button_square:hover:not(:disabled) {
	background-color: rgba(55, 55, 55, 0.6);
}
.screen_title {
	background-color: #828282;
	color: #2a2a2a;
	font-family: "Open Sans", sans-serif;
	font-size: 2vh;
	text-align: center;
	min-height: 5vh;
	max-height: 5vh;
}
input,
textarea {
	background-color: rgba(47, 47, 47, 0.3);
	font-family: "Open Sans", sans-serif;
}
.search_input {
	background-color: #2a2a2a;
}
.bottom_tabs {
	min-height: 8vh;
	max-height: 8vh;
}
.bottom_tabs .isvg loaded {
	padding: 10px 10px;
}
.bottom_tabs .tab_title {
	padding: 0 15px;
}
.bottom_tabs .tab {
	background-color: #0a0a0a;
	flex-direction: row;
	padding: 20px 20px;
}
.bottom_tabs .tab:hover {
	background-color: #6c6c6c;
}
.bottom_tabs .button_large,
.bottom_tabs .button_flat,
.bottom_tabs .button_square {
	/* font-size: 1.2em; */
	/* border-radius: 5px; */
	
	margin: 1vh;
}
.character_options .list,
.tone_options .list,
.actions_options .list,
.whisper_options .list {
	background-color: #151515;
}
}
