css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.hordes.fr") {
/* v0.5 */

#modules{top:5px;
margin-left:410px;
position:absolute;
z-index:5;
padding-left:5px;
padding-right:5px;
background-color:#5c2b20;
-moz-border-radius:0px;
outline:1px solid black;
border:1px solid #f0d79e;
}

#modules p,
#modules form input,
#modules form label,
#modules h1 span{display:none;
}
#modules:hover p,
#modules:hover form input,
#modules:hover form label{display:block;
}
#modules:hover h1 span{display:inline;
}
#modules h1{
margin:0px  !important;
 padding : 0px !important;
height:auto !important;
font-size:8pt !important;
text-transform:none !important;
font-variant:small-caps;
background:none !important;
cursor:help;
}
#modules:hover h1{border-bottom:1px solid #b37c4a;
margin-bottom:5px;
}
#modules ul{margin:0px ; padding : 0px;
float:left;
position:relative;
margin-bottom:8px;
list-style:none;
}
#modules ul li{margin:0px ; padding : 0px;
}
#modules a.link{display:block;
width:160px;
height:18px;
overflow:hidden;
text-decoration:none;
}
#modules a.link span{text-decoration:underline;
}
#modules a.link:hover{background-color:#696486;
}
#modules a.link img{width:16px;
height:16px;
vertical-align:-25%;
}
#modules form input{margin:0px ; padding : 0px;
float:left;
position:relative;
margin-bottom:8px;
list-style:none;
}

#modules p{margin:0px ; padding : 0px;
width:300px;
margin-bottom:5px;
font-size:9pt;
line-height:11pt;
text-align:justify;
}

/*
#modules:hover form label{margin-left:18px;
}*/


}
