css:
/* i really want this to be global */
#bjd_bottom_detail,
#gwd_website_icon,
#mini_price_history_detail .app-tuiguang,
#gwd_mini_compare,
#gwd_mini_remind,
#gwd_mini_seckill,
#trend_com_detail {
    display: none !important
}

#gwd_minibar .minibar-tab {
	border-left-width: 0 !important;
}

#mini_price_history_detail {
	max-width: 460px;
	top: 37px;
	border-top: 1px solid #ddd;
}

#big_tooltip {
	width: 100%;
}

#mini_price_history_detail .mini_price_history_detail_wrapper {
	width: 100%;
}

.gwd-minibar-bg {
	padding-top: 5px;
}

#gwd_minibar {
	height: 34px;
	line-height: 34px;
}

#gwd_minibar .minibar-tab {
	height: 34px;
}

#mini_price_history {
	text-align: left;
	cursor: pointer;
	width: 95px !important;
	padding-left: 5px !important;
}

.dsj-content {
	top: 6px;
	left: 100px;
	cursor: default;
	user-select: none;
}

.dsj-content .dsj-promo-link {
	cursor: default !important;
}

.dsj-content .dsj-promo-link {
	width: 182px !important;
}

@-moz-document domain("jd.com") {
/* 修复京东导航栏宽度异常问题 */
#shop-head, .userDefinedArea {
	overflow: hidden;
	position: relative;
}


.jdm-tbar-tab-qrcode {
	display: none;
}

#gwd_minibar {
	width: 588px;
}

.dsj-content {
	position: absolute !important;
	width: 488px !important;
}

.dsj-content .dsj-promo-link {
	width: 270px !important;
}

#mini_price_history_detail {
	width: 588px;
	max-width: 588px;
}

#mini_price_history_detail .max-min-priinfo {
	width: 526px;
}

#mini_price_history_detail_chart {
	left: 64px;
}

#mini_price_history_detail_btn {
	margin-left: 57px;
}
}

@-moz-document domain("taobao.com") {
#gwd_minibar {
	width: 460px;
}
}

@-moz-document domain("tmall.com") {
#gwd_minibar {
	width: 508px;
}

#mini_price_history_detail {
	width: 508px;
	max-width: 508px;
}

#mini_price_history_detail_chart {
	left: 25px;
}
}
