css:
@-moz-document domain("urbandictionary.com") {
body.generated, #dark_top, body, #urban-top-bar .top-bar-section .dropdown { background: #1D2439; }
@media only screen and (min-width: 40.0625em) { #urban-top-bar .top-bar-section li>a, #urban-top-bar .top-bar-section .dropdown.alphabet-dropdown li a, #urban-top-bar .top-bar-section .dropdown.alphabet-dropdown li span { background: #0e121f; } }
#urban-top-bar, #urban-top-bar .top-bar, #outer .def-panel, .def-panel .thumbs .up, .def-panel .thumbs .down, .def-footer .circle-link, .ad-panel, .da-panel, #outer .def-panel, #outer .panel, body div .center, .slogan-panel .like_buttons a, .header { background: #0e121f; }
.search-and-actions .main-search input, .aa-dropdown-menu, ul.pagination li:hover a, ul.pagination li a:focus, ul.pagination li:hover button, ul.pagination li button:focus, form input[type="email"], .new_ticket input[type="text"], .new_ticket input[type="email"], .new_ticket textarea { background: #07080d !important; }
form input[type="email"] { border: 0; }
.college span, .drugs span, .food span { color: #000 !important; }
.aa-dropdown-menu { box-shadow: 0 3px 3px #000; } 
.panel h4 .underline { border-color: #ffffffab; }
.panel h4 .underline { border-color: rgba(255, 255, 255, .671); }
.search-and-actions .main-search input::placeholder { color: #666771; }
#outer .def-panel, #outer .panel, .slogan-panel .like_buttons a { box-shadow: 1px 1px 2px #080a0d; }
input[type="text"]:focus, input[type="password"]:focus, input[type="date"]:focus, input[type="datetime"]:focus, input[type="datetime-local"]:focus, input[type="month"]:focus, input[type="week"]:focus, input[type="email"]:focus, input[type="number"]:focus, input[type="search"]:focus, input[type="tel"]:focus, input[type="time"]:focus, input[type="url"]:focus, input[type="color"]:focus, textarea:focus { box-shadow: 0 0 5px #000; }
.def-panel .meaning, .def-panel .example, .def-panel .contributor, .def-panel .thumbs .up, .def-panel .thumbs .down, .panel h4 .underline, .panel h1, .panel h2, .panel h3, .panel h4, .panel h5, .panel h6, .panel p, .panel li, .panel dl, .search-and-actions .main-search input, .aa-suggestion:hover, body div .center div, form input[type="email"], .new_ticket input[type="text"], .new_ticket input[type="email"], .new_ticket textarea, h1, h2, h3, h4, h5, h6, .cart-component div { color: #ffffffd4; }
.def-panel .meaning, .def-panel .example, .def-panel .contributor, .def-panel .thumbs .up, .def-panel .thumbs .down, .panel h4 .underline, .panel h1, .panel h2, .panel h3, .panel h4, .panel h5, .panel h6, .panel p, .panel li, .panel dl, .search-and-actions .main-search input, .aa-suggestion:hover, body div .center div, form input[type="email"], .new_ticket input[type="text"], .new_ticket input[type="email"], .new_ticket textarea, .cart-component div { color: rgba(255, 255, 255, .83); }
.panel div.monthly-activity { background-color: rgba(255, 255, 255, .89); }
.def-panel .thumbs svg, .def-footer .circle-link svg, .def-panel .share .svgicon svg { fill: #ffffffd4 !important; }
.def-panel .thumbs svg, .def-footer .circle-link svg, .def-panel .share .svgicon svg { fill: rgba(255, 255, 255, .83) !important; }
.def-panel .thumbs.upvoted .up, .def-panel .thumbs .up:hover, .def-panel .thumbs .up:active, .def-panel .thumbs .up.depressed { background: #297542; }
.def-panel .thumbs.downvoted .down, .def-panel .thumbs .down:hover, .def-panel .thumbs .down:active, .def-panel .thumbs .down.depressed { background: #753329; }
.def-footer .circle-link:hover, .def-footer .circle-link:active, .def-footer .circle-link.depressed, .def-footer.qtip, .aa-suggestion:hover, .main-actions .circle-button { background: #294a75; }
.def-footer.qtip { border-color: #294a75; }
.qtip .qtip-tip canvas { display: none; }
.def-footer .circle-link, .def-panel .thumbs .up, .def-panel .thumbs .down { box-shadow: 0 2px 0 #fff; }
.def-footer .circle-link, .def-panel .thumbs .up, .def-panel .thumbs .down, .def-panel .thumbs.downvoted .up { border-color: #fff; }
.def-panel .ribbon { padding: 5px; }
.def-panel .share a[data-network="facebook"]:hover .svgicon svg, .like_buttons li:nth-child(2) a:hover svg { fill: #517db7 !important; }
.like_buttons li:nth-child(2) a:hover { color: #517db7; }
.def-panel .share a[data-network="twitter"]:hover .svgicon svg, .like_buttons li:first-child a:hover svg { fill: #42abe3 !important; }
.like_buttons li:first-child a:hover { color: #43abe3; }
.def-panel .share a[data-network="share"]:hover .svgicon svg { fill: #61c459 !important; }
.def-panel .share a[data-network="heart"]:hover .svgicon svg { fill: #c4522e !important; }
blockquote { border-color: #000000ab; }
blockquote, h1, #urban-top-bar .top-bar-section .dropdown.cart-dropdown .row { border-color: rgba(0, 0, 0, .671); }
.row .top-bar .name { display: flex; }
.row .top-bar .name:after { content: url(data:image/png;base64,); }
.row .top-bar .name:after { margin-top: 11px; }
.row .top-bar .name:after { transform: translateX(-10px); }
#urban-top-bar .top-bar-section .dropdown.alphabet-dropdown::before, #urban-top-bar .top-bar-section .dropdown.cart-dropdown::before { border-color: transparent transparent #1D2439 transparent; }
#urban-top-bar .top-bar-section .dropdown.alphabet-dropdown::before, #urban-top-bar .top-bar-section .dropdown.cart-dropdown::before { filter: drop-shadow(0 -1px 0 #000); }
#chart tspan { fill: #B0B1B5; }
.panel div.monthly-activity { background-color: #0e121f; }
#chart .domain, #chart g line { stroke: #ffffffab !important; }
#chart .domain, #chart g line { stroke: rgba(255, 255, 255, .671) !important; }
}
