css:
@-moz-document url-prefix("https://theoldreader.com") {
    /* list of links to home, starred, etc */
    
    .sidebar-nav-list {
        display: none;
    }
    /* navigation pane */
    
    .sidebar-nav.well {
        border-radius: 0 !important;
        margin-left: 0 !important;
    }
    .add-icon {
        display: none !important;
    }
    .subscribe-button-container {
        padding-left: 0 !important;
        padding-right: 0 !important;
    }
    .subscribe-button-container .bg {
        display: none !important;
    }
    .subscribe .btn-primary {
        border-radius: 0 !important;
        box-shadow: none !important;
        background: #f4f4f4 !important;
        padding-left: 0 !important;
        padding-right: 0 !important;
    }
    /* Subscription entry */
    
    strong[data-bind="text: title"] {
        max-width: 100% !important;
        padding-top: 5px;
        padding-bottom: 5px;
    }
    nav {
        display: none !important;
    }
    .main-row .sidenav-cell {
        width: 220px;
    }
    .main-container {
        padding-top: 0 !important;
    }
    .body-fixed-top {
        top: 10px !important;
        left: 231px !important;
    }
    #sidebar .slide {
        padding-right: 0 !important;
    }
    #sidebar .pane {
        left: 208px !important;
    }
    .page-header {
        height: 38px !important;
        padding-bottom: 0px !important;
    }
    .page-header .clearfix {
        display: none;
    }
    .page-header div:last-child {
        display: inline;
    }
    .page-header .feedtop-buttons,
    .page-header div:last-child {
        visibility: hidden;
    }
    .page-header:hover .feedtop-buttons,
    .page-header:hover div:last-child {
        visibility: visible;
    }
    .page-header .username {
        font-size: 26px !important;
    }
    .body-fixed-top .slide {
        top: 85px !important;
    }
    .posts {
        margin-top: 0 !important;
    }
    .posts .post {
        box-shadow: none !important;
        border-radius: 0 !important;
        border: 1px solid #f4f4f4 !important;
        padding-top: 9px !important;
        padding-bottom: 1px !important;
    }
    .posts .post.unread {
        border-left: 4px solid #26687f !important;
    }
}
