css:
@-moz-document domain("e-reading.pw") {
html body {
    background-color: #595959;
}
div.forummessage,
.mainsearch INPUT {
    background-color: #ababab !important;
}
html {
    background-color: #1c0657 !important;
}
div {
    background-color: #205856 !important;
    color: black;
}
A,
A:link,
A:active {
    color: #016cca !important;
}
div.mainsearch form {
    background-color: #3c8077 !important;
}
IMG,
table,
td {
    background-color: #595959 !important;
}
BODY,
TABLE,
TD,
TR,
TH,
P,
LI,
OL,
UL,
DT,
DL,
DD,
H1,
H2,
H3,
H4,
FORM,
INPUT,
SELECT,
OPTION,
TEXTAREA,
A,
A:link,
A:visited,
A:hover,
A:active {
    font: normal 16px Tahoma, Geneva, Arial, Helvetica, sans-srif !important;
    color: #1c0657 !important;
}
}

@-moz-document url-prefix("https://www.e-reading.pw/listbooks") {
div noindex p {
    display: none !important;
}
H1 {
    color: #01d0ff !important;
}
blfooter,
.booklist td {
    background-color: #205856 !important;
}
BODY,
TABLE,
TD,
TR,
TH,
P,
LI,
OL,
UL,
DT,
DL,
DD,
H1,
H2,
H3,
H4,
FORM,
INPUT,
SELECT,
OPTION,
TEXTAREA,
A,
A:link,
A:visited,
A:hover,
A:active {
    color: #000000 !important;
}
}

@-moz-document url-prefix("https://www.e-reading.pw/bookbytypes") {
center font b {
    color: #1c7ebf !important;
}
body center font {
    color: #1c7ebf !important;
}
}

@-moz-document url-prefix("https://www.e-reading.pw/recommended"), url-prefix("https://www.e-reading.pw/top100"), url-prefix("https://www.e-reading.pw/comments") {
div noindex p {
    display: none !important;
}
H1 {
    color: #1c7ebf !important;
}
.booklist th {
    background-color: #ffd60f !important;
    color: black !important;
}
A,
A:link,
A:active,
.booklist td {
    color: #000000 !important;
}
}

@-moz-document url-prefix("https://www.e-reading.pw/comments") {
.commlist td {
    color: #ccff01 !important;
}
.commlist th {
    background-color: #595959 !important;
}
.commlist th {
    border-right: 1px solid #595959;
}
}

@-moz-document url-prefix("https://www.e-reading.pw/new") {
.booklist th {
    border-right: 0px !important;
    background-color: #074c06 !important;
    color: #fff701 !important;
}
div noindex p {
    display: none !important;
}
.A,
A:link,
A:active,
.booklist td {
    color: #000000 !important;
}
H1 {
    color: #1c7ebf !important;
}
}

@-moz-document url-prefix("https://www.e-reading.pw/forum"), url-prefix("https://www.e-reading.pw/reader"), url-prefix("https://www.e-reading.pw/writers"), url-prefix("https://www.e-reading.pw/add") {
BODY,
TABLE,
TD,
TR,
TH,
P,
LI,
OL,
UL,
DT,
DL,
DD,
H1,
H2,
H3,
H4,
FORM,
INPUT,
SELECT,
OPTION,
TEXTAREA,
A,
A:link,
A:visited,
A:hover,
A:active {
    font: normal 18px Tahoma, Geneva, Arial, Helvetica, sans-srif !important;
    color: #1c0657 !important;
}
center div p,
div ul li {
    font-size: 14px !important;
}
}

@-moz-document url-prefix("https://www.e-reading.pw/add") {
tr td input {
    background-color: #d7ff36;
}
center form textarea {
    background-color: #4e96d4;
}
center form input {
    background-color: #45ff51;
}
}

@-moz-document url-prefix("https://www.e-reading.pw/collection") {
div noindex p {
    display: none !important;
}
}

@-moz-document url-prefix("https://www.e-reading.pw/todo") {
.booklist td,
span {
    font-size: 16px !important;
    color: #000 !important;
}
}

@-moz-document url-prefix("https://www.e-reading.pw/bookreader") {
div.section {
    margin-left: 220px;
}
div.section h1 p {
    font-size: 36px !important;
}
div.section p {
    text-indent: 0;
    font-family: "PT Serif";
    font-size: 18px !important;
    margin: 0 0 1em 0;
    max-width: 43em;
    text-align: left;
    line-height: 1.4em;
}
BODY,
TABLE,
TD,
TR,
TH,
P,
LI,
OL,
UL,
DT,
DL,
DD,
H1,
H2,
H3,
H4,
FORM,
INPUT,
SELECT,
OPTION,
TEXTAREA,
A,
A:link,
A:visited,
A:hover,
A:active {
    color: #000000 !important;
}
}
