css:
/* i really want this to be global */
/* START OF CSS */
:root {
    text-decoration-skip-ink: /*[[skipInk]]*//*[[important]]*/
}
/* END OF CSS */
@-moz-document url-prefix("https://userstyles.org/styles/158916/o") {
/* Self-promo */
div[class*="recommended-styles_recommendedStylesWrapper_"]:not(#krystian3w),
div.recommendations:not(#krystian3w) {
  display: none !important;
  visibility: hidden !important;
  opacity: 0 !important;
  filter: opacity(0) !important;
  user-select: none !important;
  pointer-events: none !important;
  width: 0 !important;
  height: 0 !important;
}
div[class*="recommended-styles_recommendedStylesWrapper_"]:not(#krystian3w) * {
  width: 0 !important;
  height: 0 !important;
}
}
