css:
@-moz-document url("https://gabrielecirulli.github.io/2048/") {
html, body {
    margin: 0;
    padding: 0;
    background: #131313;
    color: #097ade;
    font-family: "Clear Sans", "Helvetica Neue", Arial, sans-serif;
    font-size: 18px;
}

.game-container {
    margin-top: 40px;
    position: relative;
    padding: 15px;
    cursor: default;
    -webkit-touch-callout: none;
    -ms-touch-callout: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    -ms-touch-action: none;
    touch-action: none;
    background: #131313;
    border-radius: 6px;
    width: 500px;
    height: 500px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

.game-message {
    display: none;
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background: rgb(19, 19, 19);
    z-index: 100;
    padding-top: 40px;
    text-align: center;
    -webkit-animation: fade-in 800ms ease 1200ms;
    -moz-animation: fade-in 800ms ease 1200ms;
    animation: fade-in 800ms ease 1200ms;
    -webkit-animation-fill-mode: both;
    -moz-animation-fill-mode: both;
    animation-fill-mode: both;
}

.score-container, .best-container {
    position: relative;
    display: inline-block;
    background: #0e0e0e;
    padding: 15px 25px;
    font-size: 25px;
    height: 25px;
    line-height: 47px;
    font-weight: bold;
    border-radius: 3px;
    color: white;
    margin-top: 8px;
    text-align: center;
}

.restart-button {
    display: inline-block;
    background: #0e0e0e;
    border-radius: 3px;
    padding: 0 20px;
    text-decoration: none;
    color: #f9f6f2;
    height: 40px;
    line-height: 42px;
    cursor: pointer;
    text-align: center;
}

.app-notice {
    position: relative;
    -webkit-animation: pop-in-big 700ms ease 2s both;
    -moz-animation: pop-in-big 700ms ease 2s both;
    animation: pop-in-big 700ms ease 2s both;
    background: #101010;
    color: white;
    padding: 18px;
    margin-top: 30px;
    height: 56px;
    box-sizing: border-box;
    border-radius: 3px;
}

.grid-cell {
    width: 106.25px;
    height: 106.25px;
    margin-right: 15px;
    float: left;
    border-radius: 3px;
    background: rgba(53, 53, 53, 0.02);
}


.pp-donate button {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    border: none;
    font: inherit;
    display: inline-block;
    background: #1b1b1a;
    border-radius: 3px;
    padding: 0 20px;
    text-decoration: none;
    color: #f9f6f2;
    height: 40px;
    line-height: 42px;
    cursor: pointer;
}

.btc-donate {
    position: relative;
    margin-left: 10px;
    display: inline-block;
    background: #1d1d1d;
    border-radius: 3px;
    padding: 0 20px;
    text-decoration: none;
    color: #f9f6f2;
    height: 40px;
    line-height: 42px;
    cursor: pointer;
}

.tile.tile-2 .tile-inner {
    background: #191918;
    box-shadow: 0 0 30px 10px rgba(243, 215, 116, 0), inset 0 0 0 1px rgba(255, 255, 255, 0);
}

.tile.tile-4 .tile-inner {
    background: #232323;
    box-shadow: 0 0 30px 10px rgba(243, 215, 116, 0), inset 0 0 0 1px rgba(255, 255, 255, 0);
}

.tile.tile-8 .tile-inner {
    color: #f9f6f2;
    background: #a04a00;
}

.tile.tile-16 .tile-inner {
    color: #f9f6f2;
    background: #e45104;
}

.tile.tile-32 .tile-inner {
    color: #f9f6f2;
    background: #9e290c;
}

.tile.tile-64 .tile-inner {
    color: #f9f6f2;
    background: #ff3000;
}

.tile.tile-128 .tile-inner {
    color: #ffffff;
    background: #ffb100;
    box-shadow: 0 0 30px 10px rgba(243, 215, 116, 0.2381), inset 0 0 0 1px rgba(255, 255, 255, 0.14286);
    font-size: 45px;
}

.tile.tile-256 .tile-inner {
    color: #000000;
    background: #efff00;
    box-shadow: 0 0 30px 10px rgba(243, 215, 116, 0.31746), inset 0 0 0 1px rgba(255, 255, 255, 0.19048);
    font-size: 45px;
}

}
