}
ul.main-nav a.active {
    background-color: lightseagreen !important;
}
div.hero div.row {
    background-color: #1C1C1C !important;
    color: palegreen !important;
}
body,
input,
textarea,
select,
article,
p.total span,
aside.project-list section div,
aside.project-list section div a,
nav.navbar,
div.track-buttons-container a span.track-inner,
div.wrapper.content nav,
div.wrapper.content div,
div.loader,
div.loading-bar,
div.master,
div.kanban-table,
div.category-config-list,
div.check span,
div.filter-list a.single-filter span,
div.summary-progress-bar,
div.summary-progress-bar div.project-points-progress,
div#discover-placeholder div,
div.admin-attributes-section section div,
div.admin-attributes-section section div h2,
div.admin-attributes-section section input,
div.tags-block input,
section.related-tasks div,
section.support,
section.main.admin-roles.admin-common p.total,
section.us-story-main-data,
section.main fieldset input,
section.wizard-step fieldset input,
section.wizard-step fieldset textarea,
section.webhooks-table.basic-table div.table-body fieldset input,
fieldset input[type="email"],
input#search-text,
input.ng-pristine,
input.ng-touched,
input.ng-dirty {
    background-color: #1C1C1C;
    color: palegreen !important;
}
a > span.icon.icon-delete:hover,
div.lightbox,
div.active {
    background-color: #1A3022 !important;
}
section.attachments div.attachments-header,
section.attachments div.attachments-header h3,
div.kanban-uses-box.task-column {
    background-color: #233562;
    color: palegreen;
}
div.items-container {
    background-color: #00290E !important;
}
div.markItUpContainer textarea,
div.ticket-status div,
a.ticket-watch-button {
    background-color: black !important;
    color: palegreen !important;
}
div.us-title,
div.us-title h2 span,
div.us-title p.external-reference,
.upvote-btn a,
section.feature-single.scrum,
section.attachments {
    background-color: #182547 !important;
    color: palegreen !important;
}
article em,
section.support,
div.kanban-task-inner {
    background-color: #161616 !important;
}
div.kanban-task.ng-pristine.ng-untouched.ng-valid.tg-scope.kanban-task-maximized {
    background-color: #045200 !important;
}
div.kanban-uses-box.task-column.ui-sortable.tg-scope {
    background-color: #313131 !important;
}
span.points-value,
div.user-story-name span {
    color: palegreen !important;
}
div.row.us-item-row.tg-scope:hover,
div.row.table-main:hover,
div.track-buttons-container a span.track-inner:hover {
    background-color: #011900 !important;
}
div.table-header div.row:hover,
.upvote-btn a:hover,
div.track-buttons-container a span.track-button-counter:hover,
section.duty-content div section:hover,
section.main.wiki section:hover {
    background-color: #071026 !important;
}
a > span.icon.icon-delete:hover,
section.wysiwyg *:hover,
section.support a:hover {
    color: #9A5555 !important;
}
.activity-diff .key,
.history-tabs,
.related-userstories-header,
.detail-title-wrapper,
.detail-header-container {
    background-color: #3a554e !important;
}
.history-tabs .history-tab.active,
h1 .project-name,
.detail-title-wrapper,
.detail-title-wrapper .detail-title-text,
.detail-title-wrapper .detail-number,
.detail-title-wrapper .detail-subject {
    color: #dedede;
}
.history-tabs .history-tab.active {
    border-bottom: 3px solid #dedede;
}
.epic-row {
    background: #000;
}
.epic-row .progress-bar {
    background: #333232;
}
.card-inner {
    background: #1d2926;
}
.card-owner-actions {
    background: rgb(29, 41, 38);
}
.card-unfold:hover {
    background: linear-gradient(to bottom, #3a554e, #1d2926);
}
tg-filter {
    background-color: #3a554e;
    border-left: 1px solid palegreen;
}
tg-filter h1 span{
    color: #dedede;
}
.filters-cats .filters-cat-single {
    color: #3a554e;
    color:palegreen;
}
.filters-cats .filters-cat-single.selected,
.filters-cats .filters-cat-single:hover {
    background-color: #3a554e;;
    color: #4e9a06;
}
.single-filter {
        background: #3a554e;
    border-bottom: 1px solid palegreen;
    padding-bottom:1px;
    border-top: 1px solid palegreen;
    opacity:1;
}
.single-filter .number {
    background: palegreen;
    position: absolute;
    right: 0;
    top: 0;
    color: #fff;

}

.single-filter:hover{
    background:transparent;
    padding-bottom:0px;
    border-bottom:2px solid palegreen;
}

.single-filter:hover .name{
    
}
.wiki-summary.summary {
    background: #3a554e;
    color: #988e8e;
}
.profile-timeline .activity-item .username:first-child,
.profile-timeline .activity-item a:first-child {
    color: #20b2aa;
}
.home-wrapper .title-bar {
    background: #555555;
    color: #fff;
}

.duty-custom-fields .custom-fields-header {
    background: #1c1c1c;
}
.story-row {   
     background: #021308;
}

.story-row .progress-bar {
    background: #333232;
}
.ticket-estimation .ticket-role-points .points,
.ticket-estimation .ticket-role-points .role,
.ticket-watchers .user-list-name {
    color: #cdcdcd;
}

span.ticket-title{
    color: #555555;
}

.navbar .nav-right .active{
    background: rgb(85, 85, 85);
}

/* Dropdown notification menu */
.topnav-dropdown-wrapper .navbar-dropdown-notifications{
    background: #000000;
    border: 1px solid #1c1c1c;
}

.topnav-dropdown-wrapper .navbar-dropdown-notifications .header {
    background: #000000;
    color: #f5f5f5;
}

.topnav-dropdown-wrapper .navbar-dropdown-notifications .notifications-list .entry .entry-content .entry a.user-link {
    color: #5b8200;
}

.topnav-dropdown-wrapper .navbar-dropdown-notifications .notifications-list .entry .entry-content > p >span{
    color:#f5f5f5;
}

/* Notification page */

.notifications-page .header{
    background: #1c1c1c;
}
.notifications-page .header .title{
    color:#f5f5f5;
}

.notifications-page .notifications-list .entry.new {
    background-color: #000000;
    border: 1px solid #e4e3e3;
}
.notifications-page .notifications-list .entry .entry-content >p > span{
    color : #e4e3e3;
}


section.attachments div.attachments-header, section.attachments div.attachments-header h3, div.kanban-uses-box.task-column {
    background-color: #1c1c1c;
    color: palegreen;
}

.single-attachment {
    background: rgb(0, 0, 0);
}

.ticket-created-by .created-by .created-date, .ticket-created-by .created-by .created-title,.ticket-header .ticket-title {
    color: #e7e6ef;
}

.comment .comment-edited {
    background: #000000;
}
tg-project-menu {
    background-color: #3a554e;
    background-image: none !important;
}

.single-project .like-button{ background:#000; }

.single-project .like-button .track-button-counter,
.single-project .like-button .track-inner {
    background: #000000;
    border: 1px solid #cecece;
}

.single-project .project-data .involved-data .title {
    background: #000000;
    border: 1px solid #cecece;
    color:#cecece;
}
.summary .number{
    color:#cecece;
}
.kanban-table-body .kanban-uses-box, .kanban-table-header .task-colum-name, .taskboard-table-body .taskboard-cards-box {
    background: #000000;
}

/* scrollbar test */
::-webkit-scrollbar {
    width: 12px;
}
 
/* Track */
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3); 
    -webkit-border-radius: 10px;
    border-radius: 10px;
}
 
/* Handle */
::-webkit-scrollbar-thumb {
    -webkit-border-radius: 10px;
    border-radius: 10px;
    background: #3a554e !important; 
    -webkit-box-shadow: inset 0 0 6px rgba(152,251,152,0.5); 
}
::-webkit-scrollbar-thumb:window-inactive {
	background: #3a554e !important; 
}
}
