/* ==UserStyle==
@name        WP Engine Dark
@namespace   mikemanger
@version     1.0.9
@homepageURL https://github.com/mikemanger/stylish-wpengine-dark-theme/
@updateURL   https://raw.githubusercontent.com/mikemanger/stylish-wpengine-dark-theme/master/wpengine-dashboard-dark.user.css
@license     CC-BY-4.0
==/UserStyle== */
@-moz-document domain(my.wpengine.com) {
	body,
	.navbar-default {
		background-color: rgb(38, 38, 38);
		color: #6a737d;
	}

	.navbar-default {
		border-color: #484848;
	}

	.shared-header .navbar-header .navbar-brand,
	footer.page-footer svg {
		filter: invert(90%);
	}

	.shared-header .navbar-nav > li.active,
	.shared-header .navbar-nav > li.open,
	.shared-header .navbar-nav > li:hover {
		border-color: #8CABC8;
	}

	a:focus,
	a:hover,
	.navbar-default .navbar-nav>.active>a,
	.navbar-default .navbar-nav>.active>a:hover,
	.navbar-default .navbar-nav>.active>a:focus,
	.navbar-default .navbar-nav>li>a:hover,
	.navbar-default .navbar-nav>li>a:focus,
	.navbar-default .navbar-nav>li>a,
	#page-dashboard table.install-list tbody a,
	#sidebar li.active a,
	#sidebar, #sidebar h2,
	#sites-sidebar #sidebar.installs li a,
	footer.page-footer ul.nav-links a {
		color: #6a737d;
	}

	#page-sites .sites-table .site-name a,
	#page-sites .sites-table .panel-footer .install-name a,
	#sites-sidebar #multi_env_site_sidebar li.environment-nav a.environment-name,
	.navbar-default .navbar-nav>.open>a,
	.navbar-default .navbar-nav>.open>a:hover,
	.navbar-default .navbar-nav>.open>a:focus
	.navbar-default .navbar-nav>li>a {
		color: #8CABC8 !important;
	}

	.panel,
	.panel-heading,
	.panel-footer {
		background: #1d1d1d !important;
		color: #6a737d !important;
	}

	#page_heading,
	.panel-default,
	.panel-body,
	.panel-widget,
	.panel-footer,
	#page-dashboard table.install-list thead th,
	#page-dashboard table.install-list tbody tr+tr {
		border-color: #484848 !important;
	}

	.panel-widget .panel-heading a {
		color: #2a8792;
	}

	#pages-checkpoints table.checkpoints tr.load-error td {
		color: #f00;
	}

	table,
	table.dataTable tbody tr,
	.gray-field {
		background-color: #262626 !important;
	}

	#page-dashboard .usage table {
		border: #484848;
	}

	table.table.striped-high-contrast>thead>tr>th,
	table.table.striped-high-contrast>tbody>tr:nth-child(2n),
	#page-dashboard .usage table tr:nth-child(odd) {
		background-color: #393939;
	}

	#page-dashboard .usage table tr:nth-child(2n) {
		background: #484848;
	}

	#page-overview table.install-detail tr,
	#sites-sidebar #multi_env_site_sidebar li.environment-nav,
	table,
	.table,
	footer.page-footer {
		border-color: #484848;
	}

	#page-user-activities #user_activities table .activity-filter .dropdown-toggle {
		color: #ccc;
		border-color: #666;
	}

	#page-overview table.install-detail td.row-label {
		background-color: #393939;
	}

	.btn-primary {
		background-color: #8cabc8;
	}
	.btn-bevel-white:before {
		border-top-color: #1d1d1d;
	}

	#digital-media-platform .panel-body .btn,
	#digital-media-platform .panel-body #page-user-activities #user_activities table .activity-filter .filter-actions button,
	#page-user-activities #user_activities table .activity-filter .filter-actions #digital-media-platform .panel-body button,
	#digital-media-platform .panel-body #page-user-activities #user_activities table .activity-filter .filter-actions a,
	#page-user-activities #user_activities table .activity-filter .filter-actions #digital-media-platform .panel-body a {
		background-color: #8cabc8;
		border-color: #8cabc8;
	}

	.table-striped>tbody>tr:nth-child(even)>td,
	.table-striped>tbody>tr:nth-child(even)>th {
		background-color: inherit;
	}

	.table-striped>tbody>tr:nth-child(odd)>td,
	.table-striped>tbody>tr:nth-child(odd)>th {
		background-color: #1d1d1d !important;
	}

	table.table.striped-high-contrast > thead,
	table.table.striped-high-contrast>tbody>tr>td+td,
	.log-viewer .search-form,
	.table>thead>tr>th,
	.table>thead>tr>td,
	.table>tbody>tr>th,
	.table>tbody>tr>td,
	.table>tfoot>tr>th,
	.table>tfoot>tr>td,
	.log-viewer table.dataTable.no-footer {
		border-color: #484848 !important;
	}
}
