css:
@-moz-document domain("slickdeals.net") {
body { 
    background: #31393F;
    color: lightgray;
}

h1, h2, h3 { color: lightgray; }

.categoryBar, .announcementBar, .categoryBar + .announcementBar {
    background: #161a1d;
    color: white;
}
.categoryBar a, .announcementBar a, .categoryBar + .announcementBar a {
    color: white;
}

#fpMainContent .gridHeading > h1,
#fpMainContent .gridHeading > h2, 
#fpMainContent .gridHeading .prefHeading {
    color: lightgray;
}

#fpMainContent .gridCategory .fpGridBox {
    color: lightgray;
    background: #161a1d;
    border-color: black;
}
#fpMainContent .gridCategory ul.gridDeals .fpGridBox .fpItem .itemImageAndName .itemTitle,
#mainColumn #fpMainContent .gridCategory:not([data-module-name="Featured Deals"]):not(#fpNewsAndArticles) ul.gridDeals .fpGridBox .fpItem .itemImageAndName .itemTitle,
#mainColumn #fpMainContent .gridCategory:not([data-module-name="Featured Deals"]):not(#fpNewsAndArticles) ul.gridDeals .fpGridBox .itemInfoLine .priceLine .itemPrice {
    color: lightgray !important;
}
#fpMainContent .gridCategory .grid .fpItem .itemInfoLine,
#mainColumn #fpMainContent #featureDealsAndCoupons .fpGridBox .fpItem .itemInfoLine .itemPrice {
    color: lightgray;
}
#fpMainContent .gridCategory ul.gridDeals .fpGridBox .fpItem .itemBottomRow {
    background: #161a1d !important;
}

.viewDetailsBtn {
    background-color: #31393F;
    color: lightgray !important;
    border-color: black;
}
.viewDetailsBtn:hover {
    background-color: #434e56;
    color: lightgray !important;
}

#fpMainContent .gridCategory ul.gridDeals .fpGridBox .fpItem .itemBottomRow.personalizedDeal {
    background-color: #31393F !important;
    color: lightgray !important;
    border-top: 1px solid black !important;
}
.tooltipstered {
    color: lightgray;
}
#crt-adblock-a {
    display: none !important;
    min-height: 0 !important;
}

.sideGridBox, .sideGridBox h3 {
    color: lightgray;
    background: #161a1d;
    border-color: black;
}
.sideGridBox h3 > a, .sideGridBox .subBoxContent a.split {
    color: lightgray;
}

.box {
    background: #161a1d;
    color: lightgray;
}
.shopDeals > ul > li,
.shopDeals ul.subCat {
    background: #161a1d;
    color: lightgray;
}
.shopDeals > ul > li a,
.shopDeals ul.subCat a {
    color: lightgray;
}
.facetedNav .section .header .sectionTitle {
    color: lightgray;
}
#dealTitle h1, #dealPrice, #dealStatsNew, #detailsTop .textDescription {
    color: lightgray;
}

#detailContents .addCommentContainer, .addCommentContainer {
    color: lightgray;
    background-color: #161a1d;
    border-color: black;
}

.postTextContent, .postUsername, .postUsername a {
    color: lightgray;
}

.popularDeal h1, .popularDeal h2, .popularDeal h3, .popularDeal .aboutAuthor, .authorName > .label > a {
    color: lightgray;
}
}
