css:
@-moz-document regexp(".*\\b(box\\.com\\/folder)\\b.*") {
.file-list-item-wrapper {
        font-size: .9em;
        height: 20px;
    }
    .file-list .tbl-list-item {
        border-top: 1px solid transparent;
        border-bottom: 1px solid #EAEAEA;
        height: 20px;
        margin-top: 0;
        width: 100%;
    }
    .tbl-list-item {
        height: 20px;
        line-height: 20px;
    }
    .tbl-list-item:hover {
        height: 20px;
        line-height: 20px;
    }
    .page-body {
        margin-top: 0;
        margin-bottom: 0;
        top: -21px;
    }
    .file-icon {
        max-width: 20px;
        max-height: 22px;
        height: auto;
        object-fit: contain;
    }
    .file-list-icon svg {
        max-height: 20px;
    }
    .tbl-list-header {
        border-bottom: 1px solid #EAEAEA;
        height: 20px;
        line-height: 20px;
    }
    .btn.file-list-share-toggle {
        height: 15px;
        padding-bottom: 0;
        padding-top: 0;
        padding: 0;
        margin-top: 0;
        margin-bottom: 0;
        font-size: .9em;
        background-color: #000000;
        border: 0px solid #000000;
    }
    .btn.file-list-item-share {
        height: 20px;
        padding-right: 10px;
        padding-bottom: 0;
        padding-top: 0;
        margin-top: 0;
        margin-bottom: 0;
        padding-left: 0;
        font-size: .9em;
        background-color: #000000;
        border: 0 solid #000000;
    }
    .is-list-view .tbl-list-item .file-list-item-actions .file-list-share-toggle {
        padding-left: 0px;
        padding: 0px;
    }
    .is-list-view .tbl-list-item .file-list-item-actions .file-list-item-context-menu {
        font-size: 22px;
        padding-top: 0;
        padding-right: 5px;
        padding-bottom: 10px;
        padding-left: 5px;
    }
    .btn.file-list-item-context-menu {
        height: 10px;
        padding-top: 15;
        font-size: .9em;
        background-color: #000000;
        border: 0 solid #000000;
    }
    .menu-wrapper .menu {
        background: #fff;
        border: 1px solid #ccc;
        border-radius: 2px;
        box-shadow: 0 0 0 rgba(0, 0, 0, .1);
        display: none;
        font-size: 1em;
        list-style: none;
        margin: 0;
        opacity: 0;
        padding: 0 0;
        position: absolute;
        -webkit-transition: opacity 75ms, -webkit-transform 125ms;
        transition: opacity 75ms, transform 125ms;
        z-index: 120;
    }
    .menu-wrapper .menu .menu-option {
        line-height: 12px;
        overflow-x: hidden;
        padding: 5px 30px 1px 10px;
        position: relative;
        text-align: left;
        text-overflow: ellipsis;
        white-space: nowrap;
        -ms-flex: 1 1 auto;
        -webkit-flex: 1 1 auto;
        flex: 1 1 auto;
    }
}
