css:
@-moz-document domain("86.popmundo.com"), domain("85.popmundo.com"), domain("83.popmundo.com"), domain("84.popmundo.com"), domain("www.popmundo.com"), domain("www88.popmundo.com") {
a {
    color: #d4d5d5 !important;
}

input[type=submit], input[type=button] {
    background: #999 !important;
}

input.round, textarea.round, select.round, div.editor textarea, .roundright, .roundleft {
    background: #999 !important;
    border: 1px solid #999;
}

hr {
    background-color: #333 !important;
}

body {
    background-color: #000 !important;
    background-image: none;
}

div#header {
    background-image: none;
    background: #111 !important;
    padding-top: 15px;
    padding-bottom: 10px;
}

div#header-commands {
    margin: -15px 22px 0 0;
}

div#character-tools {
    background-image: none;
    background: #222 !important;
}

div#top-menu {
    background-image: none;
    background: #222 !important;
}

div#top-menu-nav ul li a {
    color: #fff !important;
    padding: 9px 5px 10px 5px;
}


div#top-menu-nav ul li a.active {
    background: none;
    background: #333;
}

div#top-menu-notifications-list{
    background: #333;
}

div#top-menu-notifications-list h2, h3{
    color: #fff;
}

div#ppm-main {
    color: #fff;
    background-image: none;
    background: #111 !important;
}

div#rteavailable.ui-sortable{
    color: #000;
}

div.rteitem.ui-sortable-handle{
    color: #000;
}

div#pdsetlist.ui-sortable{
    color: #000;
}

div.content p {
    color: #ffffff !important;
}

div.box, table.data tr.even td, div.editor a.button:hover {
    background: #222 !important;
    color: #fff !important;
}

tr.odd{
    background: #333;
}

div.box p.actionbuttons, div.actionbuttons {
    border-top: 1px solid #333 !important;
}

div.editor a.button {
    background: #333 !important;
    border: 1px solid #222 !important;
    color: #000;
}

div.box h3.menu {
    color: #eee;
}

div#ppm-footer {
    background-image: none;
    background: #333 !important;
    border-bottom-left-radius: 7px;
    border-bottom-right-radius: 7px;
}

div.box h2 {
    font-size: 12px;
    background-image: none;
    background-color: #222;
    background-repeat: repeat-x;
    color: #fff !important;
    font-weight: 500;
}

div.charMainInfo {
    color: #999 !important;
}

div.charMainToolbox {
    color: #C3C3C3 !important;
}

div.charMainValues {
    color: #999 !important;
}

div.tb div.tbcw {
    background: #222 !important;
    color: #999 !important;
}

div.tbslw {
    background: url(http://i.hizliresim.com/bbkP10.png);
    background-repeat: no-repeat;
    background-position: top right;
}

div.tbbll {
    background-position: top left;
    background-repeat: no-repeat;
}


div.tb div.tbcg {
    background: #555 !important;
}

div.tbsrg {
    background: url(http://i.hizliresim.com/6nrjgP.png);
    background-position: top left;
    background-repeat: no-repeat;
}

div.progressBar, div.blueProgressBar, div.greenProgressBar, div.redProgressBar {
    border-color: #000 !important;
}

table.data th {
    background-image: none;
    background-color: #222 !important;
}

table.data th, table.data td {
    color: #ddd !important;
}

div.content h1 {
    color: #eee !important;
}

div.defaultGreeting h1 {


	color: #222 !important;
}

div.defaultGreeting p {
	color: #222 !important;
}

div.tb div.tbcy {
    background: #163466;
}

div.tbcg blockquote.quote, div.tbcy blockquote.quote {
    background: #a6064c;
    color: #fff;
}

blockquote.quote, blockquote.spoiler {
    background: #ab364c;
    margin: 5px 0;
    padding: 10px;
}

div.box div.tabs a.active {
    background: #355d6a;
    /* color: #000; */
}

label {
    cursor: default;
    color: #000;
}

}
