}
#globalNavigation {
    border-top: none;
    position: absolute !important;
    background-color: rgba(87, 88, 95, 0.22)!important;
}
.banner-notifications-wrapper {
    display: none !important;
}
.AccountNavigation .auth-link {
    color: #656E78 !important;
}
.wds-global-navigation {
    background-color: #fff;
    border-image: linear-gradient(to right, #39424d 40%, #656e78 40%) 1 0 0;
    border-top: 6px solid #656e78;
    border-width: 6px 0 0;
    box-shadow: 0 1px 0 0 rgba(204, 204, 204, 0.31);
}
.wds-notification-card__text {
    color: #ddd;
}
.wds-notification-card__text:hover {
    background-color: rgba(0, 183, 224, .1);
    color: #00b7e0;
}
.wds-global-navigation__logo,
.wds-global-navigation__link,
.wds-global-navigation__wikis-menu,
.wds-global-navigation__search,
.wds-global-navigation__account-menu,
.wds-global-navigation__user-menu,
.wds-global-navigation__notifications-menu,
.wds-global-navigation__start-a-wiki {
    background-color: rgba(255, 255, 255, 0);
}
.wds-button.wds-is-secondary {
    background-color: rgba(55, 55, 55, .2);
}
.wds-dropdown__content {
    background-color: rgba(14, 14, 14, 0.70);
}
.wds-global-navigation__search-input {
    background-color: rgba(14, 14, 14, 0.40);
}
.wds-button.wds-is-text {
    background-color: rgba(255, 255, 255, 0.2);
    color: #fff;
}
.wds-button.wds-is-text:hover:not(:disabled),
.wds-button.wds-is-text:active,
.wds-button.wds-is-text.wds-is-active {
    background-color: rgba(14, 14, 14, 0.40) !important;
    color: #ddd;
}
.wds-dropdown__content .wds-list.wds-is-linked > li > a {
    color: #ddd;
}
.wds-global-navigation__search-label {
    background-color: rgba(14, 14, 14, 0.40);
}
.wds-sign-out__button {
    background-color: rgba(224, 97, 0, .18);
    color: #bbb;
}
.wds-sign-out__button:hover {
    background-color: rgba(224, 97, 0, .35);
    color: #fff;
}
.AccountNavigation .account-navigation-item .avatar-container.anon-avatar-placeholder,
.AccountNavigation .ccount-navigation-item .avatar-container.logged-avatar-placeholder {
    background-image: url("data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20xmlns%3Axlink%3D%22http%3A%2F%2Fwww.w3.org%2F1999%2Fxlink%22%20x%3D%220px%22%20y%3D%220px%22%20viewBox%3D%220%200%2018%2019%22%20style%3D%22enable-background%3Anew%200%200%2018%2019%3B%22%20xml%3Aspace%3D%22preserve%22%3E%3Cstyle%20type%3D%22text%2Fcss%22%3E.st0%7Bfill-rule%3Aevenodd%3Bclip-rule%3Aevenodd%3Bfill%3A%23666F79%3B%7D%3C%2Fstyle%3E%3Cpath%20class%3D%22st0%22%20d%3D%22M11.2%2C12.6c0.8-0.8%2C1.4-2.1%2C2-3.6c0.4-0.9%2C0.4-4.7%2C0.2-5.4c-0.7-2.3-2.4-3-4.4-3c0%2C0%2C0%2C0%2C0%2C0s0%2C0%2C0%2C0c-2%2C0-3.7%2C0.6-4.4%2C3C4.4%2C4.3%2C4.5%2C8.1%2C4.8%2C9c0.6%2C1.5%2C1.2%2C2.8%2C2%2C3.6c-3%2C0.5-6.1%2C2.9-6.1%2C2.9v2.8l8.3%2C0l8.3%2C0v-2.8C17.3%2C15.5%2C14.2%2C13.1%2C11.2%2C12.6z%22%2F%3E%3C%2Fsvg%3E");
}
.AccountNavigation .account-navigation-item .avatar-container.logged-avatar,
.AccountNavigation .account-navigation-item .avatar-container.logged-avatar-placeholder {
    border-radius: 2px 6px;
}
#GlobalNavigationWallNotifications .notification .avatars .avatar {
    border: none;
    border-radius: 2px 6px;
}
.ChatModule .chat-whos-here .avatar {
    border-radius: 6px 2px;
}
table.wallfeed td img.avatar {
    border-radius: 6px 2px;
}
.Forum .ThreadList .avatar {
    border-radius: 6px 2px;
}
.Forum .DiscussionBox .avatar {
    border-radius: 6px 2px;
}
.WikiaRail .ForumActivityModule .avatar,
.WikiaRail .ForumParticipationModule .avatar {
    border-radius: 6px 2px;
}
.global-navigation .start-wikia-container .start-wikia {
    border: 1px solid #656E78!important;
    color: #656E78!important;
}
.AccountNavigation .account-navigation-item .links-container .chevron {
    border-color: #656E78 transparent transparent!important;
}
.hubs-entry-point {
    background-image: url("data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20height%3D%2213%22%20width%3D%2216%22%20xmlns%3Axlink%3D%22http%3A%2F%2Fwww.w3.org%2F1999%2Fxlink%22%20version%3D%221.1%22%20x%3D%220px%22%20y%3D%220px%22%20viewBox%3D%220%200%2016%2016%22%20enable-background%3D%22new%200%200%2016%2016%22%20xml%3Aspace%3D%22preserve%22%3E%3Cg%3E%3Crect%20x%3D%220%22%20y%3D%220%22%20fill%3D%22rgb%28197%2C%20206%2C%20217%29%22%20width%3D%2216.5%22%20height%3D%223%22%2F%3E%3Crect%20x%3D%220%22%20y%3D%225.9%22%20fill%3D%22rgb%28197%2C%20206%2C%20217%29%22%20width%3D%2216.5%22%20height%3D%223%22%2F%3E%3Crect%20x%3D%220%22%20y%3D%2211.8%22%20fill%3D%22rgb%28197%2C%20206%2C%20217%29%22%20width%3D%2216.5%22%20height%3D%223%22%2F%3E%3C%2Fg%3E%3C%2Fsvg%3E%0A")!important;
}
.hubs-entry-point::after {
    border-color: #656E78 transparent transparent!important;
}
.global-navigation {
    background-color: #FFF!important;
    box-shadow: 0px 0px 0px 2px rgba(14, 14, 14, 0.70)!important;
}
.global-nav-search-input-wrapper .search-input {
    background-color: rgba(14, 14, 14, 0.29);
}
.global-nav-search-input-wrapper .search-button {
    background-color: rgba(14, 14, 14, 0.29);
}
.AccountNavigation .user-menu > li > a {
    background-color: rgba(14, 14, 14, 0.70);
    border-top: 1px solid #c8d2d7;
    color: #4169e1;
}
.WikiHeader > nav .subnav-3 li {
    background: rgba(20, 20, 20, 0.50) !important;
}
#WikiHeader .subnav-2a,
#WikiHeader .subnav-3 li a {
    color: #ddd;
    text-shadow: 0px 0px 2px #000;
}
.WikiHeader > nav .subnav {
    background: rgba(20, 20, 20, 0.50) !important;
}
.UserProfileMasthead .masthead-avatar .avatar {
    max-height: 150px;
    max-width: 150px;
    border-radius: 4px 20px;
}
.UserProfileMasthead .masthead-avatar {
    left: 12px;
}
.UserStatsMenu .info img {
    border-radius: 6px 2px;
}
.Wall .avatar {
    border-radius: 6px 2px;
}
.avatar {
    border-radius: 6px 2px;
}
#GlobalNavigationWallNotifications .notifications-for-wiki .notifications-for-wiki-list a {
    color: #ddd
}
#GlobalNavigationWallNotifications .notifications-for-wiki .notifications-for-wiki-list a:hover {
    color: #00b7e0;
}
#GlobalNavigationWallNotifications .notification:hover h4,
#GlobalNavigationWallNotifications .notification:hover .avatars {
    background-color: rgba(0, 183, 224, .1);
}
#GlobalNavigationWallNotifications {
    background-color: rgba(14, 14, 14, 0.70);
}
#GlobalNavigationWallNotifications .notification time {
    color: #bbb;
}
#GlobalNavigationWallNotifications .notifications-for-wiki .notifications-wiki-header {
    background-color: rgba(140, 140, 140, 0.40);
}
#theme-solarized-light {
    background-color: #3a3a3a;
    color: #586e75;
}
.ace-solarized-light {
    background-color: #3a3a3a;
    color: #586E75;
}
.hubs-menu .hub-links {
    background: rgba(14, 14, 14, 0.80);
}
.hubs-menu .hub-link .label {
    background-color: #151515
}
.hubs-menu .hub-menu-section strong {
    color: #ffa500
}
.hubs-menu .hub-menu-section a {
    color: #4169e1
}
.hubs-menu .hub-link .label {
    color: #4169e1
}
.hubs-menu .hub-link.active .label {
    color: #fff;
}
.wds-global-navigation__search-input {
    color: #ddd;
}
.wds-dropdown__content:not(.wds-is-not-scrollable) .wds-list::after,
.wds-dropdown__content:not(.wds-is-not-scrollable) .wds-list::before {
    background-color: transparent;
}

.wds-global-navigation__search-input[type="search"] {
    background-color: rgba(220, 220, 220, 0.22);
}
}

@-moz-document domain("wikia.com") {
#WikiaTopAds,
#top-right-boxad-wrapper,
#WikiaArticleBottomAd,
#WikiaAdInContentPlaceHolder,
#recirculation-footer-index-container,
#WikiaFooter,
#mixed-content-footer,
.WikiaAdInContent,
.wikia-ad,
.home-top-right-ads,
div[id^="br_video_player_"],
.FIOnDemandWrapper,
.WikiaTopAds,
.WikiaFooter.notoolbar section,
.WikiaBarWrapper.notoolbar section,
.global-footer .branding,
.wds-global-footer,
.show-global-footer .wds-global-footer,
.wds-global-navigation__link,
.featured-video__wrapper,
.recirculation-horizontal.recirculation-incontent {
    display: none !important;
}
}
