css:
@-moz-document domain(sahibinden.com)
{
	
  a
  {
    color: #9eb4f5;
  }

  .homepage .homepage-content li img
  {
    border: 1px solid #d90000;
  }

  .homepage .vitrin-list.popular-links li a, .homepage .interesting-ads.popular-links li a
  {
    border: 1px solid #dfdfdf;
    color: #bbb;
  }

  .footer-container .footer .warning-text
  {
    color: #888;
    background-color: #000;
  }

  .header-container, .my-account-tabs, .classifiedDetailContent .classifiedOtherBoxes .classifiedUserContent, .footer-container, footer .warningText, footer,
  #searchResultsTable tbody tr.searchResultsItem, #searchResultsGallery td.searchResultsGalleryItem, #classifiedProperties,.classifiedOtherDetails .classifiedDescription>ul, 
header, .user-login.guest-buy, .guest-new-screen, .guest-new-screen .not-member, .my-account-content .individual-dashboard .my-active-last-classified, .my-account-content
  .individual-dashboard .my-active-last-classified .classified-box .classified-list-container, .my-account-content .individual-dashboard .statistics-box .my-account-box,
  .two-column,.favorite-classified-row, .favorite-classified-title, .searchResultSorted,#searchResultsTable thead td.searchResultsFirstColumn, .search-left .searchResultsCat,
  .search-filter-section dl .collapseTitle,.searchResultsRight .pageNavigator, .searchResultsRight .searchFooter
  {
    background-color: #000;
  }

  .classifiedOtherDetails .classifiedDescription li.selected,.homepage .homepage-content li a, .classifiedDetailTitle h1,#searchResultsTable tbody td,
  .address-select-wrapper .address-search-list li a
  {
    color: #fff;
  }

  html, body, h1, h2, h3, h4, h5, h6, p, ul, ol, li, a, form, dl, dt, dd, fieldset, #classifiedDescription.uiBoxContainer div, .uiBoxContainer font, .uiBoxContainer span, 
.uiBoxContainer b,.classifiedOtherDetails .classifiedDescription 
.classified-pair.all-original,.classifiedOtherDetails .uiBox, #classifiedDescription,#searchResultsTable thead td
  {
    color: #fff;
    background-color: #000;
  }

  .my-account-tabs li a
  {
    color: #888;
  }

  .classifiedBreadCrumb ul li a, .classifiedBreadCrumb ol li a
  {
    color: #76a2ff;
  }

  #searchResultsTable tbody tr.searchResultsItem:not(.premiumClassified):HOVER,a:hover
  {
    background-color: #8b8e92;
  }

  .classifiedDetailContent .classifiedOtherBoxes h5, .classifiedDetailContent .classifiedOtherBoxes .userLinks li
  {
    text-shadow: none;
  }

  .classifiedDetailStickyHeader.scroll-active
  {
    background: #888;
  }

  .classifiedDetailStickyHeader.scroll-active .classified-sticky-header h1
  {
    background: #888;
    color: #333;
  }
  a.megaPhotoRight, a.megaPhotoLeft{
	  background:none;
  }
}
