css:
@-moz-document domain("merriam-webster.com") {
.card-box, .additional-content-area, .additional-content-area .additional-items {
    background: #202020 !important;
    color: #ccc;
}

.main-container, .home-top-creative-cont, form, footer, fieldset, html, tbody, thead, th, tr, td, blockquote, .home-featured-widget, .bg-white {
    background-color: #282828 !important;
}

.headword-box .hword, h1, h2, h3, h4, h5, h6, .wgt-wod-side .ws-word-and-pron .ws-word a, .word-header h2, .inner-box-wrapper .hword, .toggle-fb-comments .fb-comment-count, ._491z, .home-top-content-area .wgt-wod-home .wh-word-and-pron .wh-word a {
    color: #c4242b;
}

*, *:after, *:before {
    color: #9b9b9b;
}

ul li a {
    color: #865702;
}

ul li a:hover {
    color: #ffa500;
}

p, .seen-and-heard-block p, .wgt-wod-side .ws-def-text, .wgt-games-side .lead, .wgt-games-side .wgt-games-side-items .content .blurb, .trending-carousel .slider-card a, .trending-carousel .slick-slide a, .home-top-content-area .wgt-wod-home .wh-def-text, .editors-picks-area a {
    color: #bbb !important;
}

.wgt-wap-side-trending .wgt-wap-side-trending-items .tw-module-teaser, .additional-content-area .additional-items a, .home-top-content-area .wgt-wap-home-trending .wgt-wap-home-trending-items .tw-module-teaser, .home-top-content-area .wgt-games-home-3min h5 a, .home-top-content-area .wgt-games-home-3min p a, .wgt-hv a {
    color: #bbb;
}

.toggle-trigger {
    background: linear-gradient(to bottom,#202020 0,#111 41%);
}

.additional-content-area .additional-items p.mell-gr235:after {
    background: linear-gradient(to right,#202020,#151515 50%,#111);
}

.seen-and-heard-block {
    background-color: #393939;
    color: #bbb !important;
}

.wgt-wap-side-trending .wgt-wap-side-trending-items .tw-module-teaser:hover, .home-top-content-area .wgt-wap-home-trending .wgt-wap-home-trending-items .tw-module-teaser:hover {
    text-decoration: underline;
    color: #d9d6d6;
}

textarea, pre, input, code {
    color:#bbb !important;
    background: #333333 !important;
}
}

@-moz-document url("https://www.merriam-webster.com/") {
div, span, a {
background-color: #282828 !important;
}
}
