css:
@-moz-document regexp('.*pl[0-9]*\.plemiona\.pl\/game\.php.*'),url-prefix(http://www.plemiona.pl) {
.forum-container > div:first-child {
    overflow:hidden;
}
.forum-container > div:first-child span.shared_forum, .forum-container > div:first-child span.forum {
    font-size:12px;
    width:30%;
    display: block !important;
    background-color: transparent !important;
    margin-bottom: 4px !important;
    padding: 0px 5px !important;
    border: none !important;
    white-space: nowrap !important;
    line-height: 150% !important;
    float:left !important;
}
.forum-container > div:first-child span.shared_forum a {
    display: block;
    background-color: #DDD;
    padding: 1px;
    border: 1px solid #000;
    white-space: nowrap;
    line-height: 150%;
}

.forum-container > div:first-child span.shared_forum.shared_selected a {
    background-color: #aaa;
}
.forum-container > div:first-child span.forum.selected a {
    background-color: #D1CBAD;
}

.forum-container > div:first-child span.forum a {
    display: block;
    background-color: #F1EBDD;
    padding: 1px;
    border: 1px solid #8c5f0d;
    white-space: nowrap;
    line-height: 150%;
}
.forum-container > div:first-child span a img {
    margin-right:2px;
    background:red;
    border-radius:50%;
    width:14px;
    height:14px;
    transform:scale(1.1);
    box-sizing:border-box;
    padding:7px;
    position:relative;
    top:2px;
    left:1px;
}
}
