css:
@-moz-document domain("searx.me") {
input.form-control {
	/*font-weight: bold;*/
	font-family: mono;
	color: brown;
	font-weight: 600;
	background: #cac3c3;
}
body  {
	background: #515151 ;
}
/*body div.container {
	background:  #8D8B90  !important
}*/
div[class^="result"]:nth-child(even) {
	background: #9D98A6 !important;
	/*border-style: solid;
	border-bottom: 10px !important; 
	border-bottom-color: black !important;
	border-left: none !important;
	border-right: none !important;
	border-top: none;*/
}
div.result:nth-child(odd) {
	background: #b6b1bd !important;
	/*border-style: dotted;
	border-bottom: 3px ; 
	border-bottom-color: black !important
	border-left: none  !important;
	border-right: none !important;
	border-top: none; */
}
p.result-content {
	color: #111;
}
div.external-link {
	color: #333;
	font-size: 13px;
	font-weight: bold;
}
span.label {
	color: #333;
	font-weight: normal !important;
}
div.col-sm-4 {
	background: gray;
}
div.panel.panel-default {
	background: #949494;
}
h4.result_header a {	
	color: blue;
	text-decoration: underline;
}
h4.result_header a:hover {
	color: brown;
}
h4.result_header span.highlight {
	background: none ;
}
p span.highlight {
	background: #b0a8a8 !important;
	color: #2a2ae0 ;
}
select {
	color: #000 !important
}
p.text-muted {
	color: #ddd
}
div#categories label {
	color: #1b975e ;
	font-family: arial, open sans, verdana;
	font-size: 14px;
}
#categories input[type=checkbox]:checked+label {
	color: #1e8e60;
	font-size: 15px; 
}


}
